    53â†’/**
    54â†’ * è§„èŒƒåŒ–é¡¹ç›®è·¯å¾„ä¸º Claude Code æ ¼å¼
    55â†’ * Normalize project path to Claude Code format
    56â†’ *
    57â†’ * Windows:   D:\Projects\MyProject -> d--Projects-MyProject
    58â†’ * Linux/Mac: /home/user/projects/my-project -> -home-user-projects-my-project
    59â†’ */
    60â†’function normalizeProjectPath(projectPath) {
    61â†’  if (process.platform === 'win32') {
    62â†’    // Windows: Replace backslashes and colons with dashes, preserve case
    63â†’    return projectPath
    64â†’      .replace(/\\/g, '-')
    65â†’      .replace(/:/g, '-')
    66â†’      .replace(/\./g, '-');    // Replace dots with dashes
    67â†’  } else {
    68â†’    // Linux/Mac: Replace forward slashes with dashes, preserve case
    69â†’    // Note: Claude Code adds a leading dash for Unix paths
    70â†’    return projectPath
    71â†’      .replace(/^\//, '-')      // Replace leading slash with dash
    72â†’      .replace(/\//g, '-')      // Replace remaining slashes with dashes
    73â†’      .replace(/\./g, '-');     // Replace dots with dashes
    74â†’  }
    75â†’}
