    91â†’}
    92â†’
    93â†’/**
    94â†’ * åˆ›å»ºç¬¦å·é“¾æ¥ (è·¨å¹³å°)
    95â†’ * Create symbolic link (cross-platform)
    96â†’ */
    97â†’function createSymlink(target, linkPath) {
    98â†’  if (process.platform === 'win32') {
    99â†’    // Windows: ä½¿ç”¨ junction (ä¸éœ€è¦ç®¡ç†å‘˜æƒé™)
   100â†’    // Windows: Use junction (no admin privileges required)
   101â†’    fs.symlinkSync(target, linkPath, 'junction');
   102â†’  } else {
   103â†’    // Unix: ä½¿ç”¨ç¬¦å·é“¾æ¥
   104â†’    // Unix: Use symbolic link
   105â†’    fs.symlinkSync(target, linkPath);
   106â†’  }
   107â†’}
   108â†’
   109â†’/**
   110â†’ * æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥ï¼ˆå¹¶éªŒè¯é“¾æ¥æŒ‡å‘ï¼‰
   111â†’ * Check if path is a symbolic link (and verify link target)
   112â†’ */
   113â†’function isSymlink(symlinkPath, expectedTarget) {
   114â†’  try {
   115â†’    const stats = fs.lstatSync(symlinkPath);
   116â†’
   117â†’    // Unix: æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥
   118â†’    if (process.platform !== 'win32') {
   119â†’      return stats.isSymbolicLink();
   120â†’    }
   121â†’
   122â†’    // Windows: éœ€è¦åŒºåˆ† junction å’Œæ™®é€šç›®å½•
   123â†’    // æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥æˆ– junction
   124â†’    if (stats.isSymbolicLink()) {
   125â†’      return true;
   126â†’    }
   127â†’
   128â†’    // å¯¹äº Windowsï¼Œå¦‚æœæ˜¯ç›®å½•ï¼Œè¿˜éœ€è¦æ£€æŸ¥æ˜¯å¦ä¸º junction
   129â†’    // å¹¶ä¸”éªŒè¯å…¶æŒ‡å‘æ˜¯å¦æ­£ç¡®
   130â†’    if (stats.isDirectory()) {
   131â†’      try {
   132â†’        // è¯»å–é“¾æ¥ç›®æ ‡
   133â†’        const target = fs.readlinkSync(symlinkPath);
   134â†’        // å¦‚æœèƒ½è¯»å–åˆ°é“¾æ¥ç›®æ ‡ï¼Œè¯´æ˜æ˜¯ junction æˆ–ç¬¦å·é“¾æ¥
   135â†’        // å¦‚æœæä¾›äº†æœŸæœ›ç›®æ ‡ï¼Œåˆ™éªŒè¯æ˜¯å¦åŒ¹é…
   136â†’        if (expectedTarget) {
   137â†’          return path.resolve(target) === path.resolve(expectedTarget);
   138â†’        }
   139â†’        return true;
   140â†’      } catch {
   141â†’        // å¦‚æœè¯»å–é“¾æ¥å¤±è´¥ï¼Œè¯´æ˜æ˜¯æ™®é€šç›®å½•ï¼Œä¸æ˜¯é“¾æ¥
   142â†’        return false;
   143â†’      }
   144â†’    }
   145â†’
   146â†’    return false;
   147â†’  } catch {
   148â†’    return false;
   149â†’  }
   150â†’}
   151â†’
   152â†’/**
   153â†’ * ç§»åŠ¨ç›®å½• (é€’å½’)
   154â†’ * Move directory (recursive)
   155â†’ */
   156â†’function moveDirectory(src, dest) {
   157â†’  // åˆ›å»ºç›®æ ‡ç›®å½•
   158â†’  fs.mkdirSync(dest, { recursive: true });
   159â†’
   160â†’  // é€’å½’å¤åˆ¶æ‰€æœ‰æ–‡ä»¶å’Œå­ç›®å½•
   161â†’  const entries = fs.readdirSync(src, { withFileTypes: true });
   162â†’
   163â†’  for (const entry of entries) {
   164â†’    const srcPath = path.join(src, entry.name);
   165â†’    const destPath = path.join(dest, entry.name);
   166â†’
   167â†’    if (entry.isDirectory()) {
   168â†’      moveDirectory(srcPath, destPath);
   169â†’    } else {
   170â†’      fs.copyFileSync(srcPath, destPath);
   171â†’    }
   172â†’  }
   173â†’
   174â†’  // åˆ é™¤æºç›®å½•
   175â†’  fs.rmSync(src, { recursive: true, force: true });
   176â†’}
   177â†’
   178â†’/**
   179â†’ * åˆå¹¶ä¸¤ä¸ªç›®å½•çš„æ–‡ä»¶
   180â†’ * Merge files from two directories
   181â†’ */
   182â†’function mergeDirectories(src, dest) {
   183â†’  // ç¡®ä¿ç›®æ ‡ç›®å½•å­˜åœ¨
   184â†’  fs.mkdirSync(dest, { recursive: true });
   185â†’
   186â†’  // è¯»å–æºç›®å½•ä¸­çš„æ‰€æœ‰æ–‡ä»¶
   187â†’  const entries = fs.readdirSync(src, { withFileTypes: true });
   188â†’
   189â†’  let mergedCount = 0;
   190â†’
   191â†’  for (const entry of entries) {
   192â†’    const srcPath = path.join(src, entry.name);
   193â†’    const destPath = path.join(dest, entry.name);
   194â†’
   195â†’    if (entry.isDirectory()) {
   196â†’      // é€’å½’åˆå¹¶å­ç›®å½•
   197â†’      mergeDirectories(srcPath, destPath);
   198â†’    } else {
   199â†’      // å¦‚æœç›®æ ‡æ–‡ä»¶ä¸å­˜åœ¨ï¼Œåˆ™å¤åˆ¶ï¼›å¦‚æœå­˜åœ¨ï¼Œè·³è¿‡ï¼ˆä¿ç•™ç›®æ ‡æ–‡ä»¶ï¼‰
   200â†’      if (!fs.existsSync(destPath)) {
   201â†’        fs.copyFileSync(srcPath, destPath);
   202â†’        mergedCount++;
   203â†’      }
   204â†’    }
   205â†’  }
   206â†’
   207â†’  return mergedCount;
   208â†’}
   209â†’
   210â†’/**
   211â†’ * æ¸…ç†ä¼šè¯æ–‡ä»¶å†…å®¹ä¸­çš„æ•æ„Ÿä¿¡æ¯
   212â†’ * Clean sensitive information from session file content
   213â†’ */
   214â†’function cleanSensitiveData(content) {
   215â†’  // Pattern for Anthropic API keys (normal format)
   216â†’  const apiKeyPattern = /"primaryApiKey"\s*:\s*"sk-ant-[^"]*"/g;
   217â†’
   218â†’  // Pattern for API keys within escaped JSON strings
   219â†’  const apiKeyPatternEscaped = /\\"primaryApiKey\\":\s*\\"sk-ant-[^"]*\\"/g;
   220â†’
   221â†’  // Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   222â†’  const authTokenPatternEscaped = /\\"ANTHROPIC_AUTH_TOKEN\\"\\s*:\\s*\\"[^"]*\\"/g;
   223â†’
   224â†’  // Pattern for other API keys
   225â†’  const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\s*:\s*"[^"]*"/gi;
   226â†’
   227â†’  // Clean API keys
   228â†’  let cleaned = content.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   229â†’  cleaned = cleaned.replace(apiKeyPatternEscaped, '\\"primaryApiKey\\": \\"[REDACTED]\\"');
   230â†’  cleaned = cleaned.replace(authTokenPatternEscaped, '\\"ANTHROPIC_AUTH_TOKEN\\": \\"[REDACTED]\\"');
   231â†’  cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   232â†’
   233â†’  return cleaned;
   234â†’}
