   236â†’/**
   237â†’ * åˆå§‹åŒ–åŒæ­¥
   238â†’ * Initialize sync
   239â†’ */
   240â†’function init(projectPath, options = {}) {
   241â†’  const { folderName = '.claudeCodeSessions', force = false } = options;
   242â†’
   243â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   244â†’  const claudeProjectsDir = getClaudeProjectsDir();
   245â†’  const normalizedPath = normalizeProjectPath(projectPath);
   246â†’  const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   247â†’
   248â†’  try {
   249â†’    // æ£€æŸ¥ç¬¦å·é“¾æ¥æ˜¯å¦å·²å­˜åœ¨
   250â†’    // Check if symlink already exists
   251â†’    if (fs.existsSync(symlinkPath)) {
   252â†’      if (isSymlink(symlinkPath, historyFolder)) {
   253â†’        // å·²ç»åˆå§‹åŒ–è¿‡ï¼Œè½¬ä¸ºæ‰§è¡Œ update
   254â†’        // Already initialized, switch to update
   255â†’        info('Claude Code Chats Sync already initialized');
   256â†’        info('Running update to ensure latest configuration...');
   257â†’        console.log('');
   258â†’        updateGitFilter(projectPath, options);
   259â†’        return;
   260â†’      } else if (fs.lstatSync(symlinkPath).isDirectory()) {
   261â†’        // ç°æœ‰çœŸå®ç›®å½• - ç”¨æˆ·ä¹‹å‰ä½¿ç”¨è¿‡ Claude Code
   262â†’        // Existing real directory - user has used Claude Code before
   263â†’        const claudeStorageFiles = fs.readdirSync(symlinkPath);
   264â†’        const claudeStorageSessions = claudeStorageFiles.filter(f => f.endsWith('.jsonl'));
   265â†’
   266â†’        // æ£€æŸ¥é¡¹ç›®ä¸­æ˜¯å¦å·²ç»æœ‰ä¼šè¯æ–‡ä»¶å¤¹
   267â†’        const projectHistoryExists = fs.existsSync(historyFolder);
   268â†’        let projectSessions = [];
   269â†’        if (projectHistoryExists) {
   270â†’          projectSessions = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   271â†’        }
   272â†’
   273â†’        // åœºæ™¯1: Claudeå­˜å‚¨å’Œé¡¹ç›®ä¸­éƒ½æ²¡æœ‰ä¼šè¯æ–‡ä»¶
   274â†’        if (claudeStorageSessions.length === 0 && projectSessions.length === 0) {
   275â†’          // éƒ½æ˜¯ç©ºç›®å½•ï¼Œç›´æ¥åˆ é™¤Claudeå­˜å‚¨çš„ç›®å½•
   276â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   277â†’        }
   278â†’        // åœºæ™¯2: åªæœ‰Claudeå­˜å‚¨ä¸­æœ‰ä¼šè¯æ–‡ä»¶
   279â†’        else if (claudeStorageSessions.length > 0 && projectSessions.length === 0) {
   280â†’          if (!force) {
   281â†’            warn(`Found ${claudeStorageSessions.length} existing Claude Code session(s) in Claude's storage.`);
   282â†’            info('Use --force to move them to your project folder');
   283â†’            return;
   284â†’          }
   285â†’          // ç§»åŠ¨Claudeå­˜å‚¨çš„ç›®å½•åˆ°é¡¹ç›®æ–‡ä»¶å¤¹
   286â†’          moveDirectory(symlinkPath, historyFolder);
   287â†’          success(`Moved ${claudeStorageSessions.length} session(s) to project folder!`);
   288â†’        }
   289â†’        // åœºæ™¯3: åªæœ‰é¡¹ç›®ä¸­æœ‰ä¼šè¯æ–‡ä»¶
   290â†’        else if (claudeStorageSessions.length === 0 && projectSessions.length > 0) {
   291â†’          // åˆ é™¤Claudeå­˜å‚¨ä¸­çš„ç©ºç›®å½•
   292â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   293â†’          info(`Using existing ${projectSessions.length} session(s) from project folder`);
   294â†’        }
   295â†’        // åœºæ™¯4: Claudeå­˜å‚¨å’Œé¡¹ç›®ä¸­éƒ½æœ‰ä¼šè¯æ–‡ä»¶ - éœ€è¦åˆå¹¶
   296â†’        else if (claudeStorageSessions.length > 0 && projectSessions.length > 0) {
   297â†’          if (!force) {
   298â†’            warn(`Found sessions in both locations:`);
   299â†’            info(`  - Claude's storage: ${claudeStorageSessions.length} session(s)`);
   300â†’            info(`  - Project folder: ${projectSessions.length} session(s)`);
   301â†’            info('Use --force to merge them into your project folder');
   302â†’            return;
   303â†’          }
   304â†’          // åˆå¹¶ç›®å½•: å°†Claudeå­˜å‚¨çš„ä¼šè¯åˆå¹¶åˆ°é¡¹ç›®ä¸­
   305â†’          const mergedCount = mergeDirectories(symlinkPath, historyFolder);
   306â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   307â†’          success(`Merged ${mergedCount} session(s) from Claude's storage to project folder!`);
   308â†’          info(`Total sessions in project: ${projectSessions.length + mergedCount}`);
   309â†’        }
   310â†’      } else {
   311â†’        error(`A file exists at Claude Code location: ${symlinkPath}`);
   312â†’        return;
   313â†’      }
   314â†’    }
   315â†’
   316â†’    // åˆ›å»ºå†å²æ–‡ä»¶å¤¹ (å¦‚æœä¸å­˜åœ¨)
   317â†’    // Create history folder if it doesn't exist
   318â†’    if (!fs.existsSync(historyFolder)) {
   319â†’      fs.mkdirSync(historyFolder, { recursive: true });
   320â†’      success(`Created folder: ${historyFolder}`);
   321â†’    } else {
   322â†’      // æ˜¾ç¤ºé¡¹ç›®ä¸­çš„ä¼šè¯æ•°é‡
   323â†’      const existingSessions = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   324â†’      if (existingSessions.length > 0) {
   325â†’        info(`Using existing ${existingSessions.length} session(s) from project folder`);
   326â†’      }
   327â†’    }
   328â†’
   329â†’    // ç¡®ä¿ .claude/projects ç›®å½•å­˜åœ¨
   330â†’    // Ensure .claude/projects directory exists
   331â†’    if (!fs.existsSync(claudeProjectsDir)) {
   332â†’      fs.mkdirSync(claudeProjectsDir, { recursive: true });
   333â†’    }
   334â†’
   335â†’    // åˆ›å»ºç¬¦å·é“¾æ¥
   336â†’    // Create symbolic link
   337â†’    createSymlink(historyFolder, symlinkPath);
   338â†’
   339â†’    success('Claude Code Chats Sync initialized!');
   340â†’    info(`History folder: ${historyFolder}`);
   341â†’    info(`Linked to: ${symlinkPath}`);
   342â†’
   343â†’    // æ·»åŠ åˆ° .gitignore
   344â†’    // Add to .gitignore
   345â†’    addToGitIgnore(projectPath, folderName);
   346â†’
   347â†’    // è®¾ç½® Git è¿‡æ»¤å™¨
   348â†’    // Setup Git filter
   349â†’    setupGitFilter(projectPath, folderName, false);
   350â†’
   351â†’    // è¾“å‡ºå®‰å…¨æç¤º
   352â†’    // Show security warning
   353â†’    log('\nâš ï¸  Security Warning\n', 'yellow');
   354â†’    log('Session files may contain sensitive information:', 'yellow');
   355â†’    log('  â€¢ API keys and authentication tokens', 'yellow');
   356â†’    log('  â€¢ Proprietary code and business logic', 'yellow');
   357â†’    log('  â€¢ Private conversations', 'yellow');
   358â†’    log('  â€¢ System paths and environment details', 'yellow');
   359â†’    console.log('');
   360â†’    info('RECOMMENDED: Keep sessions in .gitignore (already added)');
   361â†’    info('Session files will be ignored by Git');
   362â†’    console.log('');
   363â†’    log('If you MUST commit session files:', 'yellow');
   364â†’    log('  1. Use environment variables for API keys:', 'yellow');
   365â†’    log('     export ANTHROPIC_AUTH_TOKEN="sk-ant-..."', 'yellow');
   366â†’    log('  2. Git filter is configured (not 100% reliable)', 'yellow');
   367â†’    log('  3. Review files before committing', 'yellow');
   368â†’    console.log('');
   369â†’    info('You are responsible for ensuring no sensitive data is committed.');
   370â†’    console.log('');
   371â†’
   372â†’  } catch (err) {
   373â†’    error(`Failed to initialize: ${err.message}`);
   374â†’  }
   375â†’}
