   377â†’/**
   378â†’ * æ£€æŸ¥åŒæ­¥çŠ¶æ€
   379â†’ * Check sync status
   380â†’ */
   381â†’function status(projectPath, options = {}) {
   382â†’  const { folderName = '.claudeCodeSessions' } = options;
   383â†’
   384â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   385â†’  const claudeProjectsDir = getClaudeProjectsDir();
   386â†’  const normalizedPath = normalizeProjectPath(projectPath);
   387â†’  const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   388â†’
   389â†’  log('\nğŸ“Š Claude Code Chats Sync Status\n', 'blue');
   390â†’
   391â†’  // æ£€æŸ¥å†å²æ–‡ä»¶å¤¹
   392â†’  // Check history folder
   393â†’  if (fs.existsSync(historyFolder)) {
   394â†’    const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   395â†’    success('History folder exists');
   396â†’    info(`   Path: ${historyFolder}`);
   397â†’    info(`   Sessions: ${files.length}`);
   398â†’  } else {
   399â†’    error('History folder not found');
   400â†’  }
   401â†’
   402â†’  // æ£€æŸ¥ç¬¦å·é“¾æ¥
   403â†’  // Check symlink
   404â†’  if (fs.existsSync(symlinkPath)) {
   405â†’    success('Symlink created');
   406â†’    info(`   Path: ${symlinkPath}`);
   407â†’  } else {
   408â†’    error('Symlink not created');
   409â†’  }
   410â†’
   411â†’  console.log('');
   412â†’}
