   414â†’/**
   415â†’ * æ·»åŠ åˆ° .gitignore
   416â†’ * Add to .gitignore
   417â†’ */
   418â†’function addToGitIgnore(projectPath, folderName = '.claudeCodeSessions') {
   419â†’  const gitignorePath = path.join(projectPath, '.gitignore');
   420â†’
   421â†’  try {
   422â†’    let content = '';
   423â†’    if (fs.existsSync(gitignorePath)) {
   424â†’      content = fs.readFileSync(gitignorePath, 'utf-8');
   425â†’    }
   426â†’
   427â†’    const ignoreEntry = `# Claude Code conversation history
   428â†’${folderName}/
