   442â†’
   443â†’    const filterScript = `#!/usr/bin/env node
   444â†’const fs = require('fs');
   445â†’const path = require('path');
   446â†’
   447â†’// Pattern for Anthropic API keys (normal format)
   448â†’const apiKeyPattern = /"primaryApiKey"\\\\s*:\\\\s*"sk-ant-[^"]*"/g;
   449â†’
   450â†’// Pattern for API keys within escaped JSON strings
   451â†’const apiKeyPatternEscaped = /\\\\\\\\"primaryApiKey\\\\\\\\"\\\\s*:\\\\\\\\s*\\\\\\"sk-ant-[^"]*\\\\\\"/g;
   452â†’
   453â†’// Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   454â†’const authTokenPatternEscaped = /\\\\"ANTHROPIC_AUTH_TOKEN\\\\"\\\\s*:\\\\\\s*\\\\"[^"]*\\\\"/g;
   455â†’
   456â†’// Pattern for other API keys
   457â†’const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\\\\s*:\\\\s*"[^"]*"/gi;
   458â†’
   459â†’let data = '';
   460â†’process.stdin.setEncoding('utf8');
   461â†’
   462â†’process.stdin.on('data', (chunk) => {
   463â†’    data += chunk;
   464â†’});
   465â†’
   466â†’process.stdin.on('end', () => {
   467â†’    let cleaned = data.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   468â†’    cleaned = cleaned.replace(apiKeyPatternEscaped, '\\\\\\\\"primaryApiKey\\\\\\\\": \\\\"[REDACTED]\\\\"');
   469â†’    cleaned = cleaned.replace(authTokenPatternEscaped, '\\\\\\\\"ANTHROPIC_AUTH_TOKEN\\\\\\\\": \\\\"[REDACTED]\\\\"');
   470â†’    cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   471â†’
   472â†’    // Replace absolute path in cwd with project name only
   473â†’    // Extract the last directory name from absolute paths
   474â†’    cleaned = cleaned.replace(
   475â†’        /"cwd"\\s*:\\s*"[^"]+?\\\\([^"\\\\]+)"/g,
   476â†’        (match, projectName) => {
   477â†’            return '"cwd": "' + projectName + '"';
   478â†’        }
   479â†’    );
   480â†’    // Also handle Unix-style paths
   481â†’    cleaned = cleaned.replace(
   482â†’        /"cwd"\\s*:\\s*"\\/[^/]+\\/([^"]+)"/g,
   483â†’        '"cwd": "$1"'
   484â†’    );
   485â†’
   486â†’    process.stdout.write(cleaned);
   487â†’});
   488â†’`;
   489â†’
   490â†’    fs.writeFileSync(filterScriptPath, filterScript, 'utf-8');
   491â†’
   492â†’    // åœ¨ Unix-like ç³»ç»Ÿä¸Šè®¾ç½®ä¸ºå¯æ‰§è¡Œ
   493â†’    // Make it executable on Unix-like systems
   494â†’    if (process.platform !== 'win32') {
