   496â†’        fs.chmodSync(filterScriptPath, 0o755);
   497â†’      } catch (e) {
   498â†’        // Ignore permission errors
   499â†’      }
   500â†’    }
   501â†’
   502â†’    // åˆ›å»º smudge è¿‡æ»¤å™¨è„šæœ¬
   503â†’    // Create smudge filter script
   504â†’    const smudgeScriptPath = path.join(projectPath, '.gitfilters', 'smudge-sessions.js');
   505â†’
   506â†’    const smudgeScript = `#!/usr/bin/env node
   507â†’const path = require('path');
   508â†’
   509â†’let data = '';
   510â†’process.stdin.setEncoding('utf8');
   511â†’
   512â†’process.stdin.on('data', (chunk) => {
   513â†’    data += chunk;
   514â†’});
   515â†’
   516â†’process.stdin.on('end', () => {
   517â†’    // Get current working directory (absolute path)
   518â†’    const currentPath = process.cwd();
   519â†’    const projectName = path.basename(currentPath);
   520â†’
   521â†’    // Replace project name in cwd with full absolute path
   522â†’    // Pattern matches "cwd":"project-name"
   523â†’    // Replaces with "cwd":"d:\\Projects\\tubo\\project-name" or "cwd":"/home/user/projects/project-name"
   524â†’    const smudged = data.replace(
   525â†’        /"cwd"\\s*:\\s*"([^"\\\/]+)"/g,
   526â†’        (match, projectNameInFile) => {
   527â†’            // Only replace if it looks like a project name (no path separators)
   528â†’            if (!projectNameInFile.includes('\\\\') && !projectNameInFile.includes('/')) {
   529â†’                // Convert to proper absolute path format for the current OS
   530â†’                const absolutePath = path.resolve(currentPath);
   531â†’                // Escape backslashes for JSON
   532â†’                const escapedPath = absolutePath.replace(/\\\\/g, '\\\\\\\\');
   533â†’                return '"cwd": "' + escapedPath + '"';
   534â†’            }
   535â†’            return match;
   536â†’        }
   537â†’    );
   538â†’
   539â†’    process.stdout.write(smudged);
   540â†’});
   541â†’`;
   542â†’
   543â†’    fs.writeFileSync(smudgeScriptPath, smudgeScript, 'utf-8');
   544â†’
   545â†’    // åœ¨ Unix-like ç³»ç»Ÿä¸Šè®¾ç½®ä¸ºå¯æ‰§è¡Œ
   546â†’    // Make it executable on Unix-like systems
   547â†’    if (process.platform !== 'win32') {
