   553â†’    }
   554â†’
   555â†’    // åœ¨ .gitconfig ä¸­é…ç½® Git è¿‡æ»¤å™¨
   556â†’    // Configure Git filter in .gitconfig
   557â†’    const gitConfigPath = path.join(projectPath, '.gitconfig');
   558â†’
   559â†’    let gitConfig = '';
