   589â†’
   590â†’    // åœ¨ .gitattributes ä¸­é…ç½®è¿‡æ»¤å™¨
   591â†’    // Configure the filter in .gitattributes
   592â†’    const gitAttributesPath = path.join(projectPath, '.gitattributes');
   593â†’
   594â†’    let gitAttributes = '';
   595â†’    if (fs.existsSync(gitAttributesPath)) {
   596â†’      gitAttributes = fs.readFileSync(gitAttributesPath, 'utf-8');
   597â†’    }
   598â†’
   599â†’    const filterLine = `${folderName}/*.jsonl filter=claude-clean`;
   600â†’
   601â†’    if (!gitAttributes.includes(filterLine)) {
   602â†’      if (gitAttributes && !gitAttributes.endsWith('\n')) {
