    53â†’/**
    54â†’ * è§„èŒƒåŒ–é¡¹ç›®è·¯å¾„ä¸º Claude Code æ ¼å¼
    55â†’ * Normalize project path to Claude Code format
    56â†’ *
    57â†’ * Windows:   D:\Projects\MyProject -> d--Projects-MyProject
    58â†’ * Linux/Mac: /home/user/projects/my-project -> -home-user-projects-my-project
    59â†’ */
    60â†’function normalizeProjectPath(projectPath) {
    61â†’  if (process.platform === 'win32') {
    62â†’    // Windows: Replace backslashes and colons with dashes, preserve case
    63â†’    return projectPath
    64â†’      .replace(/\\/g, '-')
    65â†’      .replace(/:/g, '-');
    66â†’  } else {
    67â†’    // Linux/Mac: Replace forward slashes with dashes, preserve case
    68â†’    // Note: Claude Code adds a leading dash for Unix paths
    69â†’    return projectPath
    70â†’      .replace(/^\//, '-')      // Replace leading slash with dash
    71â†’      .replace(/\//g, '-');     // Replace remaining slashes with dashes
    72â†’  }
    73â†’}
    74â†’
    75â†’/**
    76â†’ * è·å– Claude Code é¡¹ç›®ç›®å½•
    77â†’ * Get Claude Code projects directory
    78â†’ */
    79â†’function getClaudeProjectsDir() {
    80â†’  return path.join(os.homedir(), '.claude', 'projects');
    81â†’}
    82â†’
    83â†’/**
    84â†’ * è·å–é¡¹ç›®ä¸­çš„å†å²æ–‡ä»¶å¤¹è·¯å¾„
    85â†’ * Get history folder path in the project
    86â†’ */
    87â†’function getHistoryFolderPath(projectPath, folderName = '.claudeCodeSessions') {
    88â†’  return path.join(projectPath, folderName);
    89â†’}
    90â†’
    91â†’/**
    92â†’ * åˆ›å»ºç¬¦å·é“¾æ¥ (è·¨å¹³å°)
    93â†’ * Create symbolic link (cross-platform)
    94â†’ */
    95â†’function createSymlink(target, linkPath) {
    96â†’  if (process.platform === 'win32') {
    97â†’    // Windows: ä½¿ç”¨ junction (ä¸éœ€è¦ç®¡ç†å‘˜æƒé™)
    98â†’    // Windows: Use junction (no admin privileges required)
    99â†’    fs.symlinkSync(target, linkPath, 'junction');
   100â†’  } else {
   101â†’    // Unix: ä½¿ç”¨ç¬¦å·é“¾æ¥
   102â†’    // Unix: Use symbolic link
   103â†’    fs.symlinkSync(target, linkPath);
   104â†’  }
   105â†’}
   106â†’
   107â†’/**
   108â†’ * æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥ï¼ˆå¹¶éªŒè¯é“¾æ¥æŒ‡å‘ï¼‰
   109â†’ * Check if path is a symbolic link (and verify link target)
   110â†’ */
   111â†’function isSymlink(symlinkPath, expectedTarget) {
   112â†’  try {
   113â†’    const stats = fs.lstatSync(symlinkPath);
   114â†’
   115â†’    // Unix: æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥
   116â†’    if (process.platform !== 'win32') {
   117â†’      return stats.isSymbolicLink();
   118â†’    }
   119â†’
   120â†’    // Windows: éœ€è¦åŒºåˆ† junction å’Œæ™®é€šç›®å½•
   121â†’    // æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥æˆ– junction
   122â†’    if (stats.isSymbolicLink()) {
   123â†’      return true;
   124â†’    }
   125â†’
   126â†’    // å¯¹äº Windowsï¼Œå¦‚æœæ˜¯ç›®å½•ï¼Œè¿˜éœ€è¦æ£€æŸ¥æ˜¯å¦ä¸º junction
   127â†’    // å¹¶ä¸”éªŒè¯å…¶æŒ‡å‘æ˜¯å¦æ­£ç¡®
   128â†’    if (stats.isDirectory()) {
   129â†’      try {
   130â†’        // è¯»å–é“¾æ¥ç›®æ ‡
   131â†’        const target = fs.readlinkSync(symlinkPath);
   132â†’        // å¦‚æœèƒ½è¯»å–åˆ°é“¾æ¥ç›®æ ‡ï¼Œè¯´æ˜æ˜¯ junction æˆ–ç¬¦å·é“¾æ¥
   133â†’        // å¦‚æœæä¾›äº†æœŸæœ›ç›®æ ‡ï¼Œåˆ™éªŒè¯æ˜¯å¦åŒ¹é…
   134â†’        if (expectedTarget) {
   135â†’          return path.resolve(target) === path.resolve(expectedTarget);
   136â†’        }
   137â†’        return true;
   138â†’      } catch {
   139â†’        // å¦‚æœè¯»å–é“¾æ¥å¤±è´¥ï¼Œè¯´æ˜æ˜¯æ™®é€šç›®å½•ï¼Œä¸æ˜¯é“¾æ¥
   140â†’        return false;
   141â†’      }
   142â†’    }
   143â†’
   144â†’    return false;
   145â†’  } catch {
   146â†’    return false;
   147â†’  }
   148â†’}
   149â†’
   150â†’/**
   151â†’ * ç§»åŠ¨ç›®å½• (é€’å½’)
   152â†’ * Move directory (recursive)
   153â†’ */
   154â†’function moveDirectory(src, dest) {
   155â†’  // åˆ›å»ºç›®æ ‡ç›®å½•
   156â†’  fs.mkdirSync(dest, { recursive: true });
   157â†’
   158â†’  // é€’å½’å¤åˆ¶æ‰€æœ‰æ–‡ä»¶å’Œå­ç›®å½•
   159â†’  const entries = fs.readdirSync(src, { withFileTypes: true });
   160â†’
   161â†’  for (const entry of entries) {
   162â†’    const srcPath = path.join(src, entry.name);
   163â†’    const destPath = path.join(dest, entry.name);
   164â†’
   165â†’    if (entry.isDirectory()) {
   166â†’      moveDirectory(srcPath, destPath);
   167â†’    } else {
   168â†’      fs.copyFileSync(srcPath, destPath);
   169â†’    }
   170â†’  }
   171â†’
   172â†’  // åˆ é™¤æºç›®å½•
   173â†’  fs.rmSync(src, { recursive: true, force: true });
   174â†’}
   175â†’
   176â†’/**
   177â†’ * åˆå¹¶ä¸¤ä¸ªç›®å½•çš„æ–‡ä»¶
   178â†’ * Merge files from two directories
   179â†’ */
   180â†’function mergeDirectories(src, dest) {
   181â†’  // ç¡®ä¿ç›®æ ‡ç›®å½•å­˜åœ¨
   182â†’  fs.mkdirSync(dest, { recursive: true });
   183â†’
   184â†’  // è¯»å–æºç›®å½•ä¸­çš„æ‰€æœ‰æ–‡ä»¶
   185â†’  const entries = fs.readdirSync(src, { withFileTypes: true });
   186â†’
   187â†’  let mergedCount = 0;
   188â†’
   189â†’  for (const entry of entries) {
   190â†’    const srcPath = path.join(src, entry.name);
   191â†’    const destPath = path.join(dest, entry.name);
   192â†’
   193â†’    if (entry.isDirectory()) {
   194â†’      // é€’å½’åˆå¹¶å­ç›®å½•
   195â†’      mergeDirectories(srcPath, destPath);
   196â†’    } else {
   197â†’      // å¦‚æœç›®æ ‡æ–‡ä»¶ä¸å­˜åœ¨ï¼Œåˆ™å¤åˆ¶ï¼›å¦‚æœå­˜åœ¨ï¼Œè·³è¿‡ï¼ˆä¿ç•™ç›®æ ‡æ–‡ä»¶ï¼‰
   198â†’      if (!fs.existsSync(destPath)) {
   199â†’        fs.copyFileSync(srcPath, destPath);
   200â†’        mergedCount++;
   201â†’      }
   202â†’    }
   203â†’  }
   204â†’
   205â†’  return mergedCount;
   206â†’}
   207â†’
   208â†’/**
   209â†’ * æ¸…ç†ä¼šè¯æ–‡ä»¶å†…å®¹ä¸­çš„æ•æ„Ÿä¿¡æ¯
   210â†’ * Clean sensitive information from session file content
   211â†’ */
   212â†’function cleanSensitiveData(content) {
   213â†’  // Pattern for Anthropic API keys (normal format)
   214â†’  const apiKeyPattern = /"primaryApiKey"\s*:\s*"sk-ant-[^"]*"/g;
   215â†’
   216â†’  // Pattern for API keys within escaped JSON strings
   217â†’  const apiKeyPatternEscaped = /\\"primaryApiKey\\":\s*\\"sk-ant-[^"]*\\"/g;
   218â†’
   219â†’  // Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   220â†’  const authTokenPatternEscaped = /\\"ANTHROPIC_AUTH_TOKEN\\"\\s*:\\s*\\"[^"]*\\"/g;
   221â†’
   222â†’  // Pattern for other API keys
   223â†’  const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\s*:\s*"[^"]*"/gi;
   224â†’
   225â†’  // Clean API keys
   226â†’  let cleaned = content.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   227â†’  cleaned = cleaned.replace(apiKeyPatternEscaped, '\\"primaryApiKey\\": \\"[REDACTED]\\"');
   228â†’  cleaned = cleaned.replace(authTokenPatternEscaped, '\\"ANTHROPIC_AUTH_TOKEN\\": \\"[REDACTED]\\"');
   229â†’  cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   230â†’
   231â†’  return cleaned;
   232â†’}
   233â†’
   234â†’/**
   235â†’ * åˆå§‹åŒ–åŒæ­¥
   236â†’ * Initialize sync
   237â†’ */
   238â†’function init(projectPath, options = {}) {
   239â†’  const { folderName = '.claudeCodeSessions', force = false } = options;
   240â†’
   241â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   242â†’  const claudeProjectsDir = getClaudeProjectsDir();
   243â†’  const normalizedPath = normalizeProjectPath(projectPath);
   244â†’  const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   245â†’
   246â†’  try {
   247â†’    // æ£€æŸ¥ç¬¦å·é“¾æ¥æ˜¯å¦å·²å­˜åœ¨
   248â†’    // Check if symlink already exists
   249â†’    if (fs.existsSync(symlinkPath)) {
   250â†’      if (isSymlink(symlinkPath, historyFolder)) {
   251â†’        success('Claude Code Chats Sync already initialized');
   252â†’        info(`History folder: ${historyFolder}`);
   253â†’        info(`Linked to: ${symlinkPath}`);
   254â†’        return;
   255â†’      } else if (fs.lstatSync(symlinkPath).isDirectory()) {
   256â†’        // ç°æœ‰çœŸå®ç›®å½• - ç”¨æˆ·ä¹‹å‰ä½¿ç”¨è¿‡ Claude Code
   257â†’        // Existing real directory - user has used Claude Code before
   258â†’        const claudeStorageFiles = fs.readdirSync(symlinkPath);
   259â†’        const claudeStorageSessions = claudeStorageFiles.filter(f => f.endsWith('.jsonl'));
   260â†’
   261â†’        // æ£€æŸ¥é¡¹ç›®ä¸­æ˜¯å¦å·²ç»æœ‰ä¼šè¯æ–‡ä»¶å¤¹
   262â†’        const projectHistoryExists = fs.existsSync(historyFolder);
   263â†’        let projectSessions = [];
   264â†’        if (projectHistoryExists) {
   265â†’          projectSessions = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   266â†’        }
   267â†’
   268â†’        // åœºæ™¯1: Claudeå­˜å‚¨å’Œé¡¹ç›®ä¸­éƒ½æ²¡æœ‰ä¼šè¯æ–‡ä»¶
   269â†’        if (claudeStorageSessions.length === 0 && projectSessions.length === 0) {
   270â†’          // éƒ½æ˜¯ç©ºç›®å½•ï¼Œç›´æ¥åˆ é™¤Claudeå­˜å‚¨çš„ç›®å½•
   271â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   272â†’        }
   273â†’        // åœºæ™¯2: åªæœ‰Claudeå­˜å‚¨ä¸­æœ‰ä¼šè¯æ–‡ä»¶
   274â†’        else if (claudeStorageSessions.length > 0 && projectSessions.length === 0) {
   275â†’          if (!force) {
   276â†’            warn(`Found ${claudeStorageSessions.length} existing Claude Code session(s) in Claude's storage.`);
   277â†’            info('Use --force to move them to your project folder');
   278â†’            return;
   279â†’          }
   280â†’          // ç§»åŠ¨Claudeå­˜å‚¨çš„ç›®å½•åˆ°é¡¹ç›®æ–‡ä»¶å¤¹
   281â†’          moveDirectory(symlinkPath, historyFolder);
   282â†’          success(`Moved ${claudeStorageSessions.length} session(s) to project folder!`);
   283â†’        }
   284â†’        // åœºæ™¯3: åªæœ‰é¡¹ç›®ä¸­æœ‰ä¼šè¯æ–‡ä»¶
   285â†’        else if (claudeStorageSessions.length === 0 && projectSessions.length > 0) {
   286â†’          // åˆ é™¤Claudeå­˜å‚¨ä¸­çš„ç©ºç›®å½•
   287â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   288â†’          info(`Using existing ${projectSessions.length} session(s) from project folder`);
   289â†’        }
   290â†’        // åœºæ™¯4: Claudeå­˜å‚¨å’Œé¡¹ç›®ä¸­éƒ½æœ‰ä¼šè¯æ–‡ä»¶ - éœ€è¦åˆå¹¶
   291â†’        else if (claudeStorageSessions.length > 0 && projectSessions.length > 0) {
   292â†’          if (!force) {
   293â†’            warn(`Found sessions in both locations:`);
   294â†’            info(`  - Claude's storage: ${claudeStorageSessions.length} session(s)`);
   295â†’            info(`  - Project folder: ${projectSessions.length} session(s)`);
   296â†’            info('Use --force to merge them into your project folder');
   297â†’            return;
   298â†’          }
   299â†’          // åˆå¹¶ç›®å½•: å°†Claudeå­˜å‚¨çš„ä¼šè¯åˆå¹¶åˆ°é¡¹ç›®ä¸­
   300â†’          const mergedCount = mergeDirectories(symlinkPath, historyFolder);
   301â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   302â†’          success(`Merged ${mergedCount} session(s) from Claude's storage to project folder!`);
   303â†’          info(`Total sessions in project: ${projectSessions.length + mergedCount}`);
   304â†’        }
   305â†’      } else {
   306â†’        error(`A file exists at Claude Code location: ${symlinkPath}`);
   307â†’        return;
   308â†’      }
   309â†’    }
   310â†’
   311â†’    // åˆ›å»ºå†å²æ–‡ä»¶å¤¹ (å¦‚æœä¸å­˜åœ¨)
   312â†’    // Create history folder if it doesn't exist
   313â†’    if (!fs.existsSync(historyFolder)) {
   314â†’      fs.mkdirSync(historyFolder, { recursive: true });
   315â†’      success(`Created folder: ${historyFolder}`);
   316â†’    } else {
   317â†’      // æ˜¾ç¤ºé¡¹ç›®ä¸­çš„ä¼šè¯æ•°é‡
   318â†’      const existingSessions = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   319â†’      if (existingSessions.length > 0) {
   320â†’        info(`Using existing ${existingSessions.length} session(s) from project folder`);
   321â†’      }
   322â†’    }
   323â†’
   324â†’    // ç¡®ä¿ .claude/projects ç›®å½•å­˜åœ¨
   325â†’    // Ensure .claude/projects directory exists
   326â†’    if (!fs.existsSync(claudeProjectsDir)) {
   327â†’      fs.mkdirSync(claudeProjectsDir, { recursive: true });
   328â†’    }
   329â†’
   330â†’    // åˆ›å»ºç¬¦å·é“¾æ¥
   331â†’    // Create symbolic link
   332â†’    createSymlink(historyFolder, symlinkPath);
   333â†’
   334â†’    success('Claude Code Chats Sync initialized!');
   335â†’    info(`History folder: ${historyFolder}`);
   336â†’    info(`Linked to: ${symlinkPath}`);
   337â†’
   338â†’    // æ·»åŠ åˆ° .gitignore
   339â†’    // Add to .gitignore
   340â†’    addToGitIgnore(projectPath, folderName);
   341â†’
   342â†’    // è®¾ç½® Git è¿‡æ»¤å™¨
   343â†’    // Setup Git filter
   344â†’    setupGitFilter(projectPath, folderName, false);
   345â†’
   346â†’  } catch (err) {
   347â†’    error(`Failed to initialize: ${err.message}`);
   348â†’  }
   349â†’}
