   351â†’/**
   352â†’ * æ£€æŸ¥åŒæ­¥çŠ¶æ€
   353â†’ * Check sync status
   354â†’ */
   355â†’function status(projectPath, options = {}) {
   356â†’  const { folderName = '.claudeCodeSessions' } = options;
   357â†’
   358â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   359â†’  const claudeProjectsDir = getClaudeProjectsDir();
   360â†’  const normalizedPath = normalizeProjectPath(projectPath);
   361â†’  const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   362â†’
   363â†’  log('\nğŸ“Š Claude Code Chats Sync Status\n', 'blue');
   364â†’
   365â†’  // æ£€æŸ¥å†å²æ–‡ä»¶å¤¹
   366â†’  // Check history folder
   367â†’  if (fs.existsSync(historyFolder)) {
   368â†’    const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   369â†’    success('History folder exists');
   370â†’    info(`   Path: ${historyFolder}`);
   371â†’    info(`   Sessions: ${files.length}`);
   372â†’  } else {
   373â†’    error('History folder not found');
   374â†’  }
   375â†’
   376â†’  // æ£€æŸ¥ç¬¦å·é“¾æ¥
   377â†’  // Check symlink
   378â†’  if (fs.existsSync(symlinkPath)) {
   379â†’    success('Symlink created');
   380â†’    info(`   Path: ${symlinkPath}`);
   381â†’  } else {
   382â†’    error('Symlink not created');
   383â†’  }
   384â†’
   385â†’  console.log('');
   386â†’}
