   388â†’/**
   389â†’ * æ·»åŠ åˆ° .gitignore
   390â†’ * Add to .gitignore
   391â†’ */
   392â†’function addToGitIgnore(projectPath, folderName = '.claudeCodeSessions') {
   393â†’  const gitignorePath = path.join(projectPath, '.gitignore');
   394â†’
   395â†’  try {
   396â†’    let content = '';
   397â†’    if (fs.existsSync(gitignorePath)) {
   398â†’      content = fs.readFileSync(gitignorePath, 'utf-8');
   399â†’    }
   400â†’
   401â†’    const ignoreEntry = `# Claude Code conversation history
   402â†’# Uncomment the line below to ignore session files, OR configure Git filter for safe sharing
   403â†’# ${folderName}/`;
   404â†’
   405â†’    // ä»…åœ¨ä¸å­˜åœ¨æ—¶æ·»åŠ 
   406â†’    // Only add if not already present
   407â†’    if (!content.includes(`# ${folderName}/`) && !content.includes(`${folderName}/`)) {
   408â†’      if (content && !content.endsWith('\n')) {
   409â†’        content += '\n';
   410â†’      }
   411â†’      content += `\n${ignoreEntry}\n`;
   412â†’      fs.writeFileSync(gitignorePath, content, 'utf-8');
   413â†’      success('Added .gitignore entry (commented by default)');
   414â†’    }
   415â†’  } catch (err) {
   416â†’    warn('Could not update .gitignore (not a Git repository?)');
   417â†’  }
   418â†’}
   419â†’
   420â†’/**
   421â†’ * è®¾ç½® Git è¿‡æ»¤å™¨
   422â†’ * Setup Git filter
   423â†’ */
   424â†’function setupGitFilter(projectPath, folderName = '.claudeCodeSessions', showMessage = true) {
   425â†’  try {
   426â†’    // æ£€æŸ¥æ˜¯å¦ä¸º Git ä»“åº“
   427â†’    // Check if we're in a Git repository
   428â†’    const gitDir = path.join(projectPath, '.git');
   429â†’    if (!fs.existsSync(gitDir)) {
   430â†’      warn('Not a Git repository. Git filter will not be configured.');
   431â†’      return;
   432â†’    }
   433â†’
