    91â†’/**
    92â†’ * åˆ›å»ºç¬¦å·é“¾æ¥ (è·¨å¹³å°)
    93â†’ * Create symbolic link (cross-platform)
    94â†’ */
    95â†’function createSymlink(target, linkPath) {
    96â†’  if (process.platform === 'win32') {
    97â†’    // Windows: ä½¿ç”¨ junction (ä¸éœ€è¦ç®¡ç†å‘˜æƒé™)
    98â†’    // Windows: Use junction (no admin privileges required)
    99â†’    fs.symlinkSync(target, linkPath, 'junction');
   100â†’  } else {
   101â†’    // Unix: ä½¿ç”¨ç¬¦å·é“¾æ¥
   102â†’    // Unix: Use symbolic link
   103â†’    fs.symlinkSync(target, linkPath);
   104â†’  }
   105â†’}
   106â†’
   107â†’/**
   108â†’ * æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥ï¼ˆå¹¶éªŒè¯é“¾æ¥æŒ‡å‘ï¼‰
   109â†’ * Check if path is a symbolic link (and verify link target)
   110â†’ */
   111â†’function isSymlink(symlinkPath, expectedTarget) {
   112â†’  try {
   113â†’    const stats = fs.lstatSync(symlinkPath);
   114â†’
   115â†’    // Unix: æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥
   116â†’    if (process.platform !== 'win32') {
   117â†’      return stats.isSymbolicLink();
   118â†’    }
   119â†’
   120â†’    // Windows: éœ€è¦åŒºåˆ† junction å’Œæ™®é€šç›®å½•
   121â†’    // æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥æˆ– junction
   122â†’    if (stats.isSymbolicLink()) {
   123â†’      return true;
   124â†’    }
   125â†’
   126â†’    // å¯¹äº Windowsï¼Œå¦‚æœæ˜¯ç›®å½•ï¼Œè¿˜éœ€è¦æ£€æŸ¥æ˜¯å¦ä¸º junction
   127â†’    // å¹¶ä¸”éªŒè¯å…¶æŒ‡å‘æ˜¯å¦æ­£ç¡®
   128â†’    if (stats.isDirectory()) {
   129â†’      try {
   130â†’        // è¯»å–é“¾æ¥ç›®æ ‡
   131â†’        const target = fs.readlinkSync(symlinkPath);
   132â†’        // å¦‚æœèƒ½è¯»å–åˆ°é“¾æ¥ç›®æ ‡ï¼Œè¯´æ˜æ˜¯ junction æˆ–ç¬¦å·é“¾æ¥
   133â†’        // å¦‚æœæä¾›äº†æœŸæœ›ç›®æ ‡ï¼Œåˆ™éªŒè¯æ˜¯å¦åŒ¹é…
   134â†’        if (expectedTarget) {
   135â†’          return path.resolve(target) === path.resolve(expectedTarget);
   136â†’        }
   137â†’        return true;
   138â†’      } catch {
   139â†’        // å¦‚æœè¯»å–é“¾æ¥å¤±è´¥ï¼Œè¯´æ˜æ˜¯æ™®é€šç›®å½•ï¼Œä¸æ˜¯é“¾æ¥
   140â†’        return false;
   141â†’      }
   142â†’    }
   143â†’
   144â†’    return false;
   145â†’  } catch {
   146â†’    return false;
   147â†’  }
   148â†’}
