   176â†’/**
   177â†’ * åˆå¹¶ä¸¤ä¸ªç›®å½•çš„æ–‡ä»¶
   178â†’ * Merge files from two directories
   179â†’ */
   180â†’function mergeDirectories(src, dest) {
   181â†’  // ç¡®ä¿ç›®æ ‡ç›®å½•å­˜åœ¨
   182â†’  fs.mkdirSync(dest, { recursive: true });
   183â†’
   184â†’  // è¯»å–æºç›®å½•ä¸­çš„æ‰€æœ‰æ–‡ä»¶
   185â†’  const entries = fs.readdirSync(src, { withFileTypes: true });
   186â†’
   187â†’  let mergedCount = 0;
   188â†’
   189â†’  for (const entry of entries) {
   190â†’    const srcPath = path.join(src, entry.name);
   191â†’    const destPath = path.join(dest, entry.name);
   192â†’
   193â†’    if (entry.isDirectory()) {
   194â†’      // é€’å½’åˆå¹¶å­ç›®å½•
   195â†’      mergeDirectories(srcPath, destPath);
   196â†’    } else {
   197â†’      // å¦‚æœç›®æ ‡æ–‡ä»¶ä¸å­˜åœ¨ï¼Œåˆ™å¤åˆ¶ï¼›å¦‚æœå­˜åœ¨ï¼Œè·³è¿‡ï¼ˆä¿ç•™ç›®æ ‡æ–‡ä»¶ï¼‰
   198â†’      if (!fs.existsSync(destPath)) {
   199â†’        fs.copyFileSync(srcPath, destPath);
   200â†’        mergedCount++;
   201â†’      }
   202â†’    }
   203â†’  }
   204â†’
   205â†’  return mergedCount;
   206â†’}
