   208â†’/**
   209â†’ * æ¸…ç†ä¼šè¯æ–‡ä»¶å†…å®¹ä¸­çš„æ•æ„Ÿä¿¡æ¯
   210â†’ * Clean sensitive information from session file content
   211â†’ */
   212â†’function cleanSensitiveData(content) {
   213â†’  // Pattern for Anthropic API keys (normal format)
   214â†’  const apiKeyPattern = /"primaryApiKey"\s*:\s*"sk-ant-[^"]*"/g;
   215â†’
   216â†’  // Pattern for API keys within escaped JSON strings
   217â†’  const apiKeyPatternEscaped = /\\"primaryApiKey\\":\s*\\"sk-ant-[^"]*\\"/g;
   218â†’
   219â†’  // Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   220â†’  const authTokenPatternEscaped = /\\"ANTHROPIC_AUTH_TOKEN\\"\\s*:\\s*\\"[^"]*\\"/g;
   221â†’
   222â†’  // Pattern for other API keys
   223â†’  const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\s*:\s*"[^"]*"/gi;
   224â†’
   225â†’  // Clean API keys
   226â†’  let cleaned = content.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   227â†’  cleaned = cleaned.replace(apiKeyPatternEscaped, '\\"primaryApiKey\\": \\"[REDACTED]\\"');
   228â†’  cleaned = cleaned.replace(authTokenPatternEscaped, '\\"ANTHROPIC_AUTH_TOKEN\\": \\"[REDACTED]\\"');
   229â†’  cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   230â†’
   231â†’  return cleaned;
   232â†’}
