   283â†’        }
   284â†’        // åœºæ™¯3: åªæœ‰é¡¹ç›®ä¸­æœ‰ä¼šè¯æ–‡ä»¶
   285â†’        else if (claudeStorageSessions.length === 0 && projectSessions.length > 0) {
   286â†’          // åˆ é™¤Claudeå­˜å‚¨ä¸­çš„ç©ºç›®å½•
   287â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
