   289â†’        }
   290â†’        // åœºæ™¯4: Claudeå­˜å‚¨å’Œé¡¹ç›®ä¸­éƒ½æœ‰ä¼šè¯æ–‡ä»¶ - éœ€è¦åˆå¹¶
   291â†’        else if (claudeStorageSessions.length > 0 && projectSessions.length > 0) {
   292â†’          if (!force) {
   293â†’            warn(`Found sessions in both locations:`);
   294â†’            info(`  - Claude's storage: ${claudeStorageSessions.length} session(s)`);
   295â†’            info(`  - Project folder: ${projectSessions.length} session(s)`);
   296â†’            info('Use --force to merge them into your project folder');
   297â†’            return;
   298â†’          }
   299â†’          // åˆå¹¶ç›®å½•: å°†Claudeå­˜å‚¨çš„ä¼šè¯åˆå¹¶åˆ°é¡¹ç›®ä¸­
   300â†’          const mergedCount = mergeDirectories(symlinkPath, historyFolder);
