   420â†’/**
   421â†’ * è®¾ç½® Git è¿‡æ»¤å™¨
   422â†’ * Setup Git filter
   423â†’ */
   424â†’function setupGitFilter(projectPath, folderName = '.claudeCodeSessions', showMessage = true) {
   425â†’  try {
   426â†’    // æ£€æŸ¥æ˜¯å¦ä¸º Git ä»“åº“
   427â†’    // Check if we're in a Git repository
   428â†’    const gitDir = path.join(projectPath, '.git');
   429â†’    if (!fs.existsSync(gitDir)) {
   430â†’      warn('Not a Git repository. Git filter will not be configured.');
   431â†’      return;
   432â†’    }
   433â†’
   434â†’    // åˆ›å»ºæ¸…ç†è¿‡æ»¤å™¨è„šæœ¬
   435â†’    // Create clean filter script
   436â†’    const filterScriptPath = path.join(projectPath, '.gitfilters', 'clean-sessions.js');
   437â†’    const filterDir = path.dirname(filterScriptPath);
   438â†’
   439â†’    if (!fs.existsSync(filterDir)) {
   440â†’      fs.mkdirSync(filterDir, { recursive: true });
   441â†’    }
   442â†’
   443â†’    const filterScript = `#!/usr/bin/env node
   444â†’const fs = require('fs');
   445â†’
   446â†’// Pattern for Anthropic API keys (normal format)
   447â†’const apiKeyPattern = /"primaryApiKey"\\\\s*:\\\\s*"sk-ant-[^"]*"/g;
   448â†’
   449â†’// Pattern for API keys within escaped JSON strings
   450â†’const apiKeyPatternEscaped = /\\\\\\\\"primaryApiKey\\\\\\\\"\\\\s*:\\\\\\\\s*\\\\\\"sk-ant-[^"]*\\\\\\"/g;
   451â†’
   452â†’// Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   453â†’const authTokenPatternEscaped = /\\\\"ANTHROPIC_AUTH_TOKEN\\\\"\\\\s*:\\\\\\s*\\\\"[^"]*\\\\"/g;
   454â†’
   455â†’// Pattern for other API keys
   456â†’const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\\\\s*:\\\\s*"[^"]*"/gi;
   457â†’
   458â†’let data = '';
   459â†’process.stdin.setEncoding('utf8');
   460â†’
   461â†’process.stdin.on('data', (chunk) => {
   462â†’    data += chunk;
   463â†’});
   464â†’
   465â†’process.stdin.on('end', () => {
   466â†’    let cleaned = data.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   467â†’    cleaned = cleaned.replace(apiKeyPatternEscaped, '\\\\\\\\"primaryApiKey\\\\\\\\": \\\\"[REDACTED]\\\\"');
   468â†’    cleaned = cleaned.replace(authTokenPatternEscaped, '\\\\\\\\"ANTHROPIC_AUTH_TOKEN\\\\\\\\": \\\\"[REDACTED]\\\\"');
   469â†’    cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   470â†’    process.stdout.write(cleaned);
   471â†’});
   472â†’`;
   473â†’
   474â†’    fs.writeFileSync(filterScriptPath, filterScript, 'utf-8');
   475â†’
   476â†’    // åœ¨ Unix-like ç³»ç»Ÿä¸Šè®¾ç½®ä¸ºå¯æ‰§è¡Œ
   477â†’    // Make it executable on Unix-like systems
