     1â†’#!/usr/bin/env node
     2â†’
     3â†’/**
     4â†’ * Claude Code Sync CLI
     5â†’ *
     6â†’ * è·¨å¹³å°å‘½ä»¤è¡Œå·¥å…·ï¼Œç”¨äºåŒæ­¥ Claude Code èŠå¤©ä¼šè¯åˆ°é¡¹ç›®ç›®å½•
     7â†’ * Cross-platform CLI tool to sync Claude Code chat sessions to project directory
     8â†’ *
     9â†’ * Usage:
    10â†’ *   node claude-sync-cli.js init              # Initialize sync
    11â†’ *   node claude-sync-cli.js status            # Check sync status
    12â†’ *   node claude-sync-cli.js open              # Open history folder
    13â†’ *   node claude-sync-cli.js clean             # Clean sensitive data from session files
    14â†’ *   node claude-sync-cli.js setup-git-filter  # Setup Git filter for auto-cleaning
    15â†’ */
    16â†’
    17â†’const fs = require('fs');
    18â†’const path = require('path');
    19â†’const os = require('os');
    20â†’const { execSync } = require('child_process');
    21â†’
    22â†’// ANSI é¢œè‰²ä»£ç  / ANSI color codes
    23â†’const colors = {
    24â†’  reset: '\x1b[0m',
    25â†’  red: '\x1b[31m',
    26â†’  green: '\x1b[32m',
    27â†’  yellow: '\x1b[33m',
    28â†’  blue: '\x1b[34m',
    29â†’  cyan: '\x1b[36m'
    30â†’};
    31â†’
    32â†’// å·¥å…·å‡½æ•° / Utility functions
    33â†’function log(message, color = 'reset') {
    34â†’  console.log(`${colors[color]}${message}${colors.reset}`);
    35â†’}
    36â†’
    37â†’function error(message) {
    38â†’  log(`âŒ Error: ${message}`, 'red');
    39â†’}
    40â†’
    41â†’function success(message) {
    42â†’  log(`âœ… ${message}`, 'green');
    43â†’}
    44â†’
    45â†’function info(message) {
    46â†’  log(`â„¹ï¸  ${message}`, 'cyan');
    47â†’}
    48â†’
    49â†’function warn(message) {
    50â†’  log(`âš ï¸  ${message}`, 'yellow');
    51â†’}
    52â†’
    53â†’/**
    54â†’ * è§„èŒƒåŒ–é¡¹ç›®è·¯å¾„ä¸º Claude Code æ ¼å¼
    55â†’ * Normalize project path to Claude Code format
    56â†’ *
    57â†’ * Windows:   D:\Projects\MyProject -> d--Projects-MyProject
    58â†’ * Linux/Mac: /home/user/projects/my-project -> home-user-projects-my-project
    59â†’ */
    60â†’function normalizeProjectPath(projectPath) {
    61â†’  if (process.platform === 'win32') {
    62â†’    // Windows: Replace backslashes and colons with dashes, preserve case
    63â†’    return projectPath
    64â†’      .replace(/\\/g, '-')
    65â†’      .replace(/:/g, '-');
    66â†’  } else {
    67â†’    // Linux/Mac: Replace forward slashes with dashes, preserve case
    68â†’    return projectPath
    69â†’      .replace(/^\//, '')      // Remove leading slash
    70â†’      .replace(/\//g, '-');    // Replace remaining slashes with dashes
    71â†’  }
    72â†’}
    73â†’
    74â†’/**
    75â†’ * è·å– Claude Code é¡¹ç›®ç›®å½•
    76â†’ * Get Claude Code projects directory
    77â†’ */
    78â†’function getClaudeProjectsDir() {
    79â†’  return path.join(os.homedir(), '.claude', 'projects');
    80â†’}
    81â†’
    82â†’/**
    83â†’ * è·å–é¡¹ç›®ä¸­çš„å†å²æ–‡ä»¶å¤¹è·¯å¾„
    84â†’ * Get history folder path in the project
    85â†’ */
    86â†’function getHistoryFolderPath(projectPath, folderName = '.claudeCodeSessions') {
    87â†’  return path.join(projectPath, folderName);
    88â†’}
    89â†’
    90â†’/**
    91â†’ * åˆ›å»ºç¬¦å·é“¾æ¥ (è·¨å¹³å°)
    92â†’ * Create symbolic link (cross-platform)
    93â†’ */
    94â†’function createSymlink(target, linkPath) {
    95â†’  if (process.platform === 'win32') {
    96â†’    // Windows: ä½¿ç”¨ junction (ä¸éœ€è¦ç®¡ç†å‘˜æƒé™)
    97â†’    // Windows: Use junction (no admin privileges required)
    98â†’    fs.symlinkSync(target, linkPath, 'junction');
    99â†’  } else {
   100â†’    // Unix: ä½¿ç”¨ç¬¦å·é“¾æ¥
   101â†’    // Unix: Use symbolic link
   102â†’    fs.symlinkSync(target, linkPath);
   103â†’  }
   104â†’}
   105â†’
   106â†’/**
   107â†’ * æ£€æŸ¥æ˜¯å¦ä¸ºç¬¦å·é“¾æ¥
   108â†’ * Check if path is a symbolic link
   109â†’ */
   110â†’function isSymlink(symlinkPath) {
   111â†’  try {
   112â†’    const stats = fs.lstatSync(symlinkPath);
   113â†’    return stats.isSymbolicLink() || (process.platform === 'win32' && stats.isDirectory());
   114â†’  } catch {
   115â†’    return false;
   116â†’  }
   117â†’}
   118â†’
   119â†’/**
   120â†’ * ç§»åŠ¨ç›®å½• (é€’å½’)
   121â†’ * Move directory (recursive)
   122â†’ */
   123â†’function moveDirectory(src, dest) {
   124â†’  // åˆ›å»ºç›®æ ‡ç›®å½•
   125â†’  fs.mkdirSync(dest, { recursive: true });
   126â†’
   127â†’  // é€’å½’å¤åˆ¶æ‰€æœ‰æ–‡ä»¶å’Œå­ç›®å½•
   128â†’  const entries = fs.readdirSync(src, { withFileTypes: true });
   129â†’
   130â†’  for (const entry of entries) {
   131â†’    const srcPath = path.join(src, entry.name);
   132â†’    const destPath = path.join(dest, entry.name);
   133â†’
   134â†’    if (entry.isDirectory()) {
   135â†’      moveDirectory(srcPath, destPath);
   136â†’    } else {
   137â†’      fs.copyFileSync(srcPath, destPath);
   138â†’    }
   139â†’  }
   140â†’
   141â†’  // åˆ é™¤æºç›®å½•
   142â†’  fs.rmSync(src, { recursive: true, force: true });
   143â†’}
   144â†’
   145â†’/**
   146â†’ * æ¸…ç†ä¼šè¯æ–‡ä»¶å†…å®¹ä¸­çš„æ•æ„Ÿä¿¡æ¯
   147â†’ * Clean sensitive information from session file content
   148â†’ */
   149â†’function cleanSensitiveData(content) {
   150â†’  // Pattern for Anthropic API keys (normal format)
   151â†’  const apiKeyPattern = /"primaryApiKey"\s*:\s*"sk-ant-[^"]*"/g;
   152â†’
   153â†’  // Pattern for API keys within escaped JSON strings
   154â†’  const apiKeyPatternEscaped = /\\"primaryApiKey\\":\s*\\"sk-ant-[^"]*\\"/g;
   155â†’
   156â†’  // Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   157â†’  const authTokenPatternEscaped = /\\"ANTHROPIC_AUTH_TOKEN\\"\\s*:\\s*\\"[^"]*\\"/g;
   158â†’
   159â†’  // Pattern for other API keys
   160â†’  const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\s*:\s*"[^"]*"/gi;
   161â†’
   162â†’  // Clean API keys
   163â†’  let cleaned = content.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   164â†’  cleaned = cleaned.replace(apiKeyPatternEscaped, '\\"primaryApiKey\\": \\"[REDACTED]\\"');
   165â†’  cleaned = cleaned.replace(authTokenPatternEscaped, '\\"ANTHROPIC_AUTH_TOKEN\\": \\"[REDACTED]\\"');
   166â†’  cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   167â†’
   168â†’  return cleaned;
   169â†’}
   170â†’
   171â†’/**
   172â†’ * åˆå§‹åŒ–åŒæ­¥
   173â†’ * Initialize sync
   174â†’ */
   175â†’function init(projectPath, options = {}) {
   176â†’  const { folderName = '.claudeCodeSessions', force = false } = options;
   177â†’
   178â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   179â†’  const claudeProjectsDir = getClaudeProjectsDir();
   180â†’  const normalizedPath = normalizeProjectPath(projectPath);
   181â†’  const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   182â†’
   183â†’  try {
   184â†’    // æ£€æŸ¥ç¬¦å·é“¾æ¥æ˜¯å¦å·²å­˜åœ¨
   185â†’    // Check if symlink already exists
   186â†’    if (fs.existsSync(symlinkPath)) {
   187â†’      if (isSymlink(symlinkPath)) {
   188â†’        success('Claude Code Chats Sync already initialized');
   189â†’        info(`History folder: ${historyFolder}`);
   190â†’        info(`Linked to: ${symlinkPath}`);
   191â†’        return;
   192â†’      } else if (fs.lstatSync(symlinkPath).isDirectory()) {
   193â†’        // ç°æœ‰çœŸå®ç›®å½• - ç”¨æˆ·ä¹‹å‰ä½¿ç”¨è¿‡ Claude Code
   194â†’        // Existing real directory - user has used Claude Code before
   195â†’        const files = fs.readdirSync(symlinkPath);
   196â†’        const sessionFiles = files.filter(f => f.endsWith('.jsonl'));
   197â†’
   198â†’        if (sessionFiles.length > 0 && !force) {
   199â†’          warn(`Found ${sessionFiles.length} existing Claude Code session(s) in Claude's storage.`);
   200â†’          info('Use --force to move them to your project folder');
   201â†’          return;
   202â†’        }
   203â†’
   204â†’        if (sessionFiles.length > 0 && force) {
   205â†’          // ç§»åŠ¨ç°æœ‰ç›®å½•åˆ°é¡¹ç›®æ–‡ä»¶å¤¹
   206â†’          // Move existing directory to project folder
   207â†’          moveDirectory(symlinkPath, historyFolder);
   208â†’          success(`Moved ${sessionFiles.length} session(s) to project folder!`);
   209â†’        } else {
   210â†’          // ç©ºç›®å½•ï¼Œç›´æ¥åˆ é™¤
   211â†’          // Empty directory, just remove it
   212â†’          fs.rmSync(symlinkPath, { recursive: true, force: true });
   213â†’        }
   214â†’      } else {
   215â†’        error(`A file exists at Claude Code location: ${symlinkPath}`);
   216â†’        return;
   217â†’      }
   218â†’    }
   219â†’
   220â†’    // åˆ›å»ºå†å²æ–‡ä»¶å¤¹ (å¦‚æœä¸å­˜åœ¨)
   221â†’    // Create history folder if it doesn't exist
   222â†’    if (!fs.existsSync(historyFolder)) {
   223â†’      fs.mkdirSync(historyFolder, { recursive: true });
   224â†’      success(`Created folder: ${historyFolder}`);
   225â†’    }
   226â†’
   227â†’    // ç¡®ä¿ .claude/projects ç›®å½•å­˜åœ¨
   228â†’    // Ensure .claude/projects directory exists
   229â†’    if (!fs.existsSync(claudeProjectsDir)) {
   230â†’      fs.mkdirSync(claudeProjectsDir, { recursive: true });
   231â†’    }
   232â†’
   233â†’    // åˆ›å»ºç¬¦å·é“¾æ¥
   234â†’    // Create symbolic link
   235â†’    createSymlink(historyFolder, symlinkPath);
   236â†’
   237â†’    success('Claude Code Chats Sync initialized!');
   238â†’    info(`History folder: ${historyFolder}`);
   239â†’    info(`Linked to: ${symlinkPath}`);
   240â†’
   241â†’    // æ·»åŠ åˆ° .gitignore
   242â†’    // Add to .gitignore
   243â†’    addToGitIgnore(projectPath, folderName);
   244â†’
   245â†’    // è®¾ç½® Git è¿‡æ»¤å™¨
   246â†’    // Setup Git filter
   247â†’    setupGitFilter(projectPath, folderName, false);
   248â†’
   249â†’  } catch (err) {
   250â†’    error(`Failed to initialize: ${err.message}`);
   251â†’  }
   252â†’}
   253â†’
   254â†’/**
   255â†’ * æ£€æŸ¥åŒæ­¥çŠ¶æ€
   256â†’ * Check sync status
   257â†’ */
   258â†’function status(projectPath, options = {}) {
   259â†’  const { folderName = '.claudeCodeSessions' } = options;
   260â†’
   261â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   262â†’  const claudeProjectsDir = getClaudeProjectsDir();
   263â†’  const normalizedPath = normalizeProjectPath(projectPath);
   264â†’  const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   265â†’
   266â†’  log('\nğŸ“Š Claude Code Chats Sync Status\n', 'blue');
   267â†’
   268â†’  // æ£€æŸ¥å†å²æ–‡ä»¶å¤¹
   269â†’  // Check history folder
   270â†’  if (fs.existsSync(historyFolder)) {
   271â†’    const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   272â†’    success('History folder exists');
   273â†’    info(`   Path: ${historyFolder}`);
   274â†’    info(`   Sessions: ${files.length}`);
   275â†’  } else {
   276â†’    error('History folder not found');
   277â†’  }
   278â†’
   279â†’  // æ£€æŸ¥ç¬¦å·é“¾æ¥
   280â†’  // Check symlink
   281â†’  if (fs.existsSync(symlinkPath)) {
   282â†’    success('Symlink created');
   283â†’    info(`   Path: ${symlinkPath}`);
   284â†’  } else {
   285â†’    error('Symlink not created');
   286â†’  }
   287â†’
   288â†’  console.log('');
   289â†’}
   290â†’
   291â†’/**
   292â†’ * æ·»åŠ åˆ° .gitignore
   293â†’ * Add to .gitignore
   294â†’ */
   295â†’function addToGitIgnore(projectPath, folderName = '.claudeCodeSessions') {
   296â†’  const gitignorePath = path.join(projectPath, '.gitignore');
   297â†’
   298â†’  try {
   299â†’    let content = '';
   300â†’    if (fs.existsSync(gitignorePath)) {
   301â†’      content = fs.readFileSync(gitignorePath, 'utf-8');
   302â†’    }
   303â†’
   304â†’    const ignoreEntry = `# Claude Code conversation history
   305â†’# Uncomment the line below to ignore session files, OR configure Git filter for safe sharing
   306â†’# ${folderName}/`;
   307â†’
   308â†’    // ä»…åœ¨ä¸å­˜åœ¨æ—¶æ·»åŠ 
   309â†’    // Only add if not already present
   310â†’    if (!content.includes(`# ${folderName}/`) && !content.includes(`${folderName}/`)) {
   311â†’      if (content && !content.endsWith('\n')) {
   312â†’        content += '\n';
   313â†’      }
   314â†’      content += `\n${ignoreEntry}\n`;
   315â†’      fs.writeFileSync(gitignorePath, content, 'utf-8');
   316â†’      success('Added .gitignore entry (commented by default)');
   317â†’    }
   318â†’  } catch (err) {
   319â†’    warn('Could not update .gitignore (not a Git repository?)');
   320â†’  }
   321â†’}
   322â†’
   323â†’/**
   324â†’ * è®¾ç½® Git è¿‡æ»¤å™¨
   325â†’ * Setup Git filter
   326â†’ */
   327â†’function setupGitFilter(projectPath, folderName = '.claudeCodeSessions', showMessage = true) {
   328â†’  try {
   329â†’    // æ£€æŸ¥æ˜¯å¦ä¸º Git ä»“åº“
   330â†’    // Check if we're in a Git repository
   331â†’    const gitDir = path.join(projectPath, '.git');
   332â†’    if (!fs.existsSync(gitDir)) {
   333â†’      warn('Not a Git repository. Git filter will not be configured.');
   334â†’      return;
   335â†’    }
   336â†’
   337â†’    // åˆ›å»ºæ¸…ç†è¿‡æ»¤å™¨è„šæœ¬
   338â†’    // Create clean filter script
   339â†’    const filterScriptPath = path.join(projectPath, '.gitfilters', 'clean-sessions.js');
   340â†’    const filterDir = path.dirname(filterScriptPath);
   341â†’
   342â†’    if (!fs.existsSync(filterDir)) {
   343â†’      fs.mkdirSync(filterDir, { recursive: true });
   344â†’    }
   345â†’
   346â†’    const filterScript = `#!/usr/bin/env node
   347â†’const fs = require('fs');
   348â†’
   349â†’// Pattern for Anthropic API keys (normal format)
   350â†’const apiKeyPattern = /"primaryApiKey"\\\\s*:\\\\s*"sk-ant-[^"]*"/g;
   351â†’
   352â†’// Pattern for API keys within escaped JSON strings
   353â†’const apiKeyPatternEscaped = /\\\\\\\\"primaryApiKey\\\\\\\\"\\\\s*:\\\\\\\\s*\\\\\\"sk-ant-[^"]*\\\\\\"/g;
   354â†’
   355â†’// Pattern for ANTHROPIC_AUTH_TOKEN (escaped format)
   356â†’const authTokenPatternEscaped = /\\\\"ANTHROPIC_AUTH_TOKEN\\\\"\\\\s*:\\\\\\s*\\\\"[^"]*\\\\"/g;
   357â†’
   358â†’// Pattern for other API keys
   359â†’const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\\\\s*:\\\\s*"[^"]*"/gi;
   360â†’
   361â†’let data = '';
   362â†’process.stdin.setEncoding('utf8');
   363â†’
   364â†’process.stdin.on('data', (chunk) => {
   365â†’    data += chunk;
   366â†’});
   367â†’
   368â†’process.stdin.on('end', () => {
   369â†’    let cleaned = data.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   370â†’    cleaned = cleaned.replace(apiKeyPatternEscaped, '\\\\\\\\"primaryApiKey\\\\\\\\": \\\\"[REDACTED]\\\\"');
   371â†’    cleaned = cleaned.replace(authTokenPatternEscaped, '\\\\\\\\"ANTHROPIC_AUTH_TOKEN\\\\\\\\": \\\\"[REDACTED]\\\\"');
   372â†’    cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   373â†’    process.stdout.write(cleaned);
   374â†’});
   375â†’`;
   376â†’
   377â†’    fs.writeFileSync(filterScriptPath, filterScript, 'utf-8');
   378â†’
   379â†’    // åœ¨ Unix-like ç³»ç»Ÿä¸Šè®¾ç½®ä¸ºå¯æ‰§è¡Œ
   380â†’    // Make it executable on Unix-like systems
   381â†’    if (process.platform !== 'win32') {
   382â†’      try {
   383â†’        fs.chmodSync(filterScriptPath, 0o755);
   384â†’      } catch (e) {
   385â†’        // Ignore permission errors
   386â†’      }
   387â†’    }
   388â†’
   389â†’    // åœ¨ .gitconfig ä¸­é…ç½® Git è¿‡æ»¤å™¨
   390â†’    // Configure Git filter in .gitconfig
   391â†’    const gitConfigPath = path.join(projectPath, '.gitconfig');
   392â†’
   393â†’    let gitConfig = '';
   394â†’    if (fs.existsSync(gitConfigPath)) {
   395â†’      gitConfig = fs.readFileSync(gitConfigPath, 'utf-8');
   396â†’    }
   397â†’
   398â†’    if (!gitConfig.includes('[filter "claude-clean"]')) {
   399â†’      if (gitConfig && !gitConfig.endsWith('\n')) {
   400â†’        gitConfig += '\n';
   401â†’      }
   402â†’      gitConfig += `[filter "claude-clean"]
   403â†’	clean = node .gitfilters/clean-sessions.js
   404â†’`;
   405â†’      fs.writeFileSync(gitConfigPath, gitConfig, 'utf-8');
   406â†’    }
   407â†’
   408â†’    // åœ¨æœ¬åœ° Git é…ç½®ä¸­é…ç½®è¿‡æ»¤å™¨
   409â†’    // Configure the filter in local Git config
   410â†’    try {
   411â†’      execSync(
   412â†’        `git config filter.claude-clean.clean "node .gitfilters/clean-sessions.js"`,
   413â†’        { cwd: projectPath, stdio: 'pipe' }
   414â†’      );
   415â†’    } catch (err) {
   416â†’      warn(`Failed to configure local Git filter: ${err.message}`);
   417â†’    }
   418â†’
   419â†’    // åœ¨ .gitattributes ä¸­é…ç½®è¿‡æ»¤å™¨
   420â†’    // Configure the filter in .gitattributes
   421â†’    const gitAttributesPath = path.join(projectPath, '.gitattributes');
   422â†’
   423â†’    let gitAttributes = '';
   424â†’    if (fs.existsSync(gitAttributesPath)) {
   425â†’      gitAttributes = fs.readFileSync(gitAttributesPath, 'utf-8');
   426â†’    }
   427â†’
   428â†’    const filterLine = `${folderName}/*.jsonl filter=claude-clean`;
   429â†’
   430â†’    if (!gitAttributes.includes(filterLine)) {
   431â†’      if (gitAttributes && !gitAttributes.endsWith('\n')) {
   432â†’        gitAttributes += '\n';
   433â†’      }
   434â†’      gitAttributes += `\n# Claude Code sessions - clean sensitive data on commit\n${filterLine}\n`;
   435â†’      fs.writeFileSync(gitAttributesPath, gitAttributes, 'utf-8');
   436â†’    }
   437â†’
   438â†’    if (showMessage) {
   439â†’      success('Git filter configured');
   440â†’      info('Session files will be automatically cleaned on commit');
   441â†’      info('Original files remain unchanged. Only committed versions are cleaned.');
   442â†’    }
   443â†’
   444â†’  } catch (err) {
   445â†’    error(`Failed to setup Git filter: ${err.message}`);
   446â†’  }
   447â†’}
   448â†’
   449â†’/**
   450â†’ * æ¸…ç†ä¼šè¯æ–‡ä»¶ä¸­çš„æ•æ„Ÿæ•°æ®
   451â†’ * Clean sensitive data from session files
   452â†’ */
   453â†’function cleanSessions(projectPath, options = {}) {
   454â†’  const { folderName = '.claudeCodeSessions' } = options;
   455â†’
   456â†’  const historyFolder = getHistoryFolderPath(projectPath, folderName);
   457â†’
   458â†’  if (!fs.existsSync(historyFolder)) {
   459â†’    error('History folder does not exist');
   460â†’    return;
   461â†’  }
   462â†’
   463â†’  const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   464â†’
   465â†’  if (files.length === 0) {
   466â†’    warn('No session files to clean');
   467â†’    return;
   468â†’  }
   469â†’
   470â†’  info(`Cleaning ${files.length} session file(s)...`);
   471â†’
   472â†’  let cleanedCount = 0;
   473â†’  for (const file of files) {
   474â†’    const filePath = path.join(historyFolder, file);
   475â†’    const content = fs.readFileSync(filePath, 'utf-8');
   476â†’    const cleaned = cleanSensitiveData(content);
   477â†’
