     1â†’import * as vscode from 'vscode';
     2â†’import * as path from 'path';
     3â†’import * as fs from 'fs';
     4â†’import * as os from 'os';
     5â†’import { execSync } from 'child_process';
     6â†’
     7â†’export function activate(context: vscode.ExtensionContext) {
     8â†’    console.log('Claude Code Sync extension is now active!');
     9â†’
    10â†’    // Register commands
    11â†’    const initializeCommand = vscode.commands.registerCommand(
    12â†’        'claudeCodeSync.initialize',
    13â†’        async () => {
    14â†’            await initializeClaudeSync();
    15â†’        }
    16â†’    );
    17â†’
    18â†’    const openFolderCommand = vscode.commands.registerCommand(
    19â†’        'claudeCodeSync.openFolder',
    20â†’        () => {
    21â†’            openHistoryFolder();
    22â†’        }
    23â†’    );
    24â†’
    25â†’    const checkStatusCommand = vscode.commands.registerCommand(
    26â†’        'claudeCodeSync.checkStatus',
    27â†’        () => {
    28â†’            checkSyncStatus();
    29â†’        }
    30â†’    );
    31â†’
    32â†’    const setupFilterCommand = vscode.commands.registerCommand(
    33â†’        'claudeCodeSync.setupFilter',
    34â†’        async () => {
    35â†’            const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
    36â†’            if (!workspaceFolder) {
    37â†’                vscode.window.showErrorMessage('No workspace folder found');
    38â†’                return;
    39â†’            }
    40â†’            await setupGitFilter(workspaceFolder.uri.fsPath);
    41â†’        }
    42â†’    );
    43â†’
    44â†’    const cleanSessionsCommand = vscode.commands.registerCommand(
    45â†’        'claudeCodeSync.cleanSessions',
    46â†’        async () => {
    47â†’            const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
    48â†’            if (!workspaceFolder) {
    49â†’                vscode.window.showErrorMessage('No workspace folder found');
    50â†’                return;
    51â†’            }
    52â†’
    53â†’            const answer = await vscode.window.showWarningMessage(
    54â†’                'This will clean sensitive data (API keys) from all session files in your project.\n\n' +
    55â†’                'The original files will be modified. Make sure you have a backup if needed.\n\n' +
    56â†’                'Continue?',
    57â†’                'Clean',
    58â†’                'Cancel'
    59â†’            );
    60â†’
    61â†’            if (answer === 'Clean') {
    62â†’                await cleanSessionFiles(workspaceFolder.uri.fsPath);
    63â†’            }
    64â†’        }
    65â†’    );
    66â†’
    67â†’    context.subscriptions.push(initializeCommand, openFolderCommand, checkStatusCommand, setupFilterCommand, cleanSessionsCommand);
    68â†’
    69â†’    // Auto-initialize on workspace open if enabled
    70â†’    const config = vscode.workspace.getConfiguration('claudeCodeSync');
    71â†’    if (config.get('autoInitialize', true)) {
    72â†’        const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
    73â†’        if (workspaceFolder) {
    74â†’            autoInitializeIfNeeded(workspaceFolder.uri.fsPath);
    75â†’        }
    76â†’    }
    77â†’
    78â†’    // Create status bar item
    79â†’    const statusBarItem = vscode.window.createStatusBarItem(
    80â†’        vscode.StatusBarAlignment.Right,
    81â†’        100
    82â†’    );
    83â†’    statusBarItem.command = 'claudeCodeSync.checkStatus';
    84â†’    statusBarItem.show();
    85â†’    context.subscriptions.push(statusBarItem);
    86â†’
    87â†’    // Update status bar
    88â†’    updateStatusBar(statusBarItem);
    89â†’}
