   221â†’/**
   222â†’ * Auto-initialize if needed
   223â†’ */
   224â†’async function autoInitializeIfNeeded(projectPath: string): Promise<void> {
   225â†’    const historyFolder = getHistoryFolderPath(projectPath);
   226â†’    const claudeProjectsDir = getClaudeProjectsDir();
   227â†’    const normalizedPath = normalizeProjectPath(projectPath);
   228â†’    const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   229â†’
   230â†’    // Check if already initialized
   231â†’    if (fs.existsSync(symlinkPath) || fs.existsSync(historyFolder)) {
   232â†’        return;
   233â†’    }
   234â†’
   235â†’    // Ask user if they want to initialize
   236â†’    const answer = await vscode.window.showInformationMessage(
   237â†’        'Claude Code Chats Sync: This project is not initialized. Would you like to sync conversation history within the project?',
   238â†’        'Initialize',
   239â†’        'Not now',
   240â†’        'Never'
   241â†’    );
   242â†’
   243â†’    if (answer === 'Initialize') {
   244â†’        await initializeClaudeSync();
   245â†’    } else if (answer === 'Never') {
   246â†’        const config = vscode.workspace.getConfiguration('claudeCodeSync');
   247â†’        config.update('autoInitialize', false, vscode.ConfigurationTarget.Global);
   248â†’    }
   249â†’}
