   251â†’/**
   252â†’ * Open the history folder
   253â†’ */
   254â†’function openHistoryFolder(): void {
   255â†’    const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
   256â†’    if (!workspaceFolder) {
   257â†’        vscode.window.showErrorMessage('No workspace folder found');
   258â†’        return;
   259â†’    }
   260â†’
   261â†’    const historyFolder = getHistoryFolderPath(workspaceFolder.uri.fsPath);
   262â†’
   263â†’    if (!fs.existsSync(historyFolder)) {
   264â†’        vscode.window.showInformationMessage('History folder does not exist. Please initialize first.');
   265â†’        return;
   266â†’    }
   267â†’
   268â†’    vscode.env.openExternal(vscode.Uri.file(historyFolder));
   269â†’}
