   271â†’/**
   272â†’ * Check and display sync status
   273â†’ */
   274â†’function checkSyncStatus(): void {
   275â†’    const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
   276â†’    if (!workspaceFolder) {
   277â†’        vscode.window.showErrorMessage('No workspace folder found');
   278â†’        return;
   279â†’    }
   280â†’
   281â†’    const projectPath = workspaceFolder.uri.fsPath;
   282â†’    const historyFolder = getHistoryFolderPath(projectPath);
   283â†’    const claudeProjectsDir = getClaudeProjectsDir();
   284â†’    const normalizedPath = normalizeProjectPath(projectPath);
   285â†’    const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   286â†’
   287â†’    const status: string[] = [];
   288â†’    status.push('**Claude Code Chats Sync Status**\n');
   289â†’
   290â†’    // Check history folder
   291â†’    if (fs.existsSync(historyFolder)) {
   292â†’        const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   293â†’        status.push(`âœ… History folder: ${historyFolder}`);
   294â†’        status.push(`   Sessions: ${files.length}`);
   295â†’    } else {
   296â†’        status.push(`âŒ History folder not found`);
   297â†’    }
   298â†’
   299â†’    // Check symlink
   300â†’    if (fs.existsSync(symlinkPath)) {
   301â†’        status.push(`âœ… Symlink created: ${symlinkPath}`);
   302â†’    } else {
   303â†’        status.push(`âŒ Symlink not created`);
   304â†’    }
   305â†’
   306â†’    vscode.window.showInformationMessage(status.join('\n'), 'OK');
   307â†’}
   308â†’
   309â†’/**
   310â†’ * Update status bar
   311â†’ */
   312â†’function updateStatusBar(statusBarItem: vscode.StatusBarItem): void {
   313â†’    const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
   314â†’    if (!workspaceFolder) {
   315â†’        statusBarItem.text = '$(circle-slash) Claude Code';
   316â†’        statusBarItem.tooltip = 'No workspace folder';
   317â†’        return;
   318â†’    }
   319â†’
   320â†’    const projectPath = workspaceFolder.uri.fsPath;
   321â†’    const historyFolder = getHistoryFolderPath(projectPath);
   322â†’    const claudeProjectsDir = getClaudeProjectsDir();
   323â†’    const normalizedPath = normalizeProjectPath(projectPath);
   324â†’    const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   325â†’
   326â†’    if (fs.existsSync(symlinkPath) && fs.existsSync(historyFolder)) {
   327â†’        const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   328â†’        statusBarItem.text = `$(sync) Claude Code (${files.length})`;
   329â†’        statusBarItem.tooltip = `Synced: ${files.length} sessions`;
   330â†’    } else {
   331â†’        statusBarItem.text = '$(circle-slash) Claude Code';
   332â†’        statusBarItem.tooltip = 'Not initialized. Click to setup.';
   333â†’    }
   334â†’}
