   336â†’/**
   337â†’ * Add history folder to .gitignore with comment about Git filter
   338â†’ *
   339â†’ * Always adds the folder to .gitignore (commented by default).
   340â†’ * Users can uncomment if they want to ignore session files,
   341â†’ * or leave commented if using Git filter for safe sharing.
   342â†’ */
   343â†’async function addToGitIgnore(projectPath: string): Promise<void> {
   344â†’    const gitignorePath = path.join(projectPath, '.gitignore');
   345â†’    const config = vscode.workspace.getConfiguration('claudeCodeSync');
   346â†’    const folderName = config.get('historyFolderName', '.claudeCodeSessions');
   347â†’
   348â†’    try {
   349â†’        let content = '';
   350â†’        if (fs.existsSync(gitignorePath)) {
   351â†’            content = fs.readFileSync(gitignorePath, 'utf-8');
   352â†’        }
   353â†’
   354â†’        const ignoreEntry = `# Claude Code conversation history
   355â†’# Uncomment the line below to ignore session files, OR configure Git filter for safe sharing
   356â†’# ${folderName}/`;
   357â†’
   358â†’        // Only add if not already present
   359â†’        if (!content.includes(`# ${folderName}/`) && !content.includes(`${folderName}/`)) {
   360â†’            if (content && !content.endsWith('\n')) {
   361â†’                content += '\n';
   362â†’            }
   363â†’            content += `\n${ignoreEntry}\n`;
   364â†’            fs.writeFileSync(gitignorePath, content, 'utf-8');
   365â†’        }
   366â†’    } catch (error) {
   367â†’        // Ignore errors (no .git or no write permission)
   368â†’    }
   369â†’}
