    91â†’/**
    92â†’ * Normalize project path to Claude Code format
    93â†’ *
    94â†’ * Windows:   D:\Projects\MyProject -> d--Projects-MyProject
    95â†’ * Linux/Mac: /home/user/projects/my-project -> home-user-projects-my-project
    96â†’ */
    97â†’function normalizeProjectPath(projectPath: string): string {
    98â†’    if (process.platform === 'win32') {
    99â†’        // Windows: Replace backslashes and colons with dashes, preserve case
   100â†’        return projectPath
   101â†’            .replace(/\\/g, '-')
   102â†’            .replace(/:/g, '-');
   103â†’    } else {
   104â†’        // Linux/Mac: Replace forward slashes with dashes, preserve case
   105â†’        return projectPath
   106â†’            .replace(/^\//, '')      // Remove leading slash
   107â†’            .replace(/\//g, '-');    // Replace remaining slashes with dashes
   108â†’    }
   109â†’}
