   128â†’/**
   129â†’ * Initialize Claude Code Chats Sync for current workspace
   130â†’ */
   131â†’async function initializeClaudeSync(): Promise<void> {
   132â†’    const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
   133â†’    if (!workspaceFolder) {
   134â†’        vscode.window.showErrorMessage('No workspace folder found');
   135â†’        return;
   136â†’    }
   137â†’
   138â†’    const projectPath = workspaceFolder.uri.fsPath;
   139â†’    const historyFolder = getHistoryFolderPath(projectPath);
   140â†’    const claudeProjectsDir = getClaudeProjectsDir();
   141â†’    const normalizedPath = normalizeProjectPath(projectPath);
   142â†’    const symlinkPath = path.join(claudeProjectsDir, normalizedPath);
   143â†’
   144â†’    try {
   145â†’        // Check if symlink already exists
   146â†’        if (fs.existsSync(symlinkPath)) {
   147â†’            const stats = fs.lstatSync(symlinkPath);
   148â†’            if (stats.isSymbolicLink()) {
   149â†’                // Already initialized with a symlink
   150â†’                vscode.window.showInformationMessage('Claude Code Chats Sync already initialized');
   151â†’                return;
   152â†’            } else if (stats.isDirectory()) {
   153â†’                // Existing real directory - user has used Claude Code before
   154â†’                const files = fs.readdirSync(symlinkPath);
   155â†’                const sessionFiles = files.filter(f => f.endsWith('.jsonl'));
   156â†’
   157â†’                if (sessionFiles.length > 0) {
   158â†’                    const answer = await vscode.window.showWarningMessage(
   159â†’                        `Found ${sessionFiles.length} existing Claude Code session(s) in Claude's storage.\n\n` +
   160â†’                        `Move them to your project folder?`,
   161â†’                        'Move to Project',
   162â†’                        'Cancel'
   163â†’                    );
   164â†’
   165â†’                    if (answer === 'Move to Project') {
   166â†’                        // Move existing directory to project folder
   167â†’                        await moveDirectorySync(symlinkPath, historyFolder);
   168â†’                        vscode.window.showInformationMessage(
   169â†’                            `âœ… Moved ${sessionFiles.length} session(s) to project folder!`
   170â†’                        );
   171â†’                    } else {
   172â†’                        return;
   173â†’                    }
   174â†’                } else {
   175â†’                    // Empty directory, just remove it
   176â†’                    fs.rmSync(symlinkPath, { recursive: true, force: true });
   177â†’                }
   178â†’            } else {
   179â†’                // It's a file, not a directory - error
   180â†’                vscode.window.showErrorMessage(
   181â†’                    `A file exists at Claude Code location: ${symlinkPath}`
   182â†’                );
   183â†’                return;
   184â†’            }
   185â†’        }
   186â†’
   187â†’        // Create history folder if it doesn't exist
   188â†’        if (!fs.existsSync(historyFolder)) {
   189â†’            fs.mkdirSync(historyFolder, { recursive: true });
   190â†’            vscode.window.showInformationMessage(`Created folder: ${historyFolder}`);
   191â†’        }
   192â†’
   193â†’        // Ensure .claude/projects directory exists
   194â†’        if (!fs.existsSync(claudeProjectsDir)) {
   195â†’            fs.mkdirSync(claudeProjectsDir, { recursive: true });
   196â†’        }
   197â†’
   198â†’        // Create symbolic link from Claude location to project folder
   199â†’        const symlinkType = process.platform === 'win32' ? 'junction' : 'dir';
   200â†’        fs.symlinkSync(historyFolder, symlinkPath, symlinkType);
   201â†’
   202â†’        vscode.window.showInformationMessage(
   203â†’            `âœ… Claude Code Chats Sync initialized!\n` +
   204â†’            `History folder: ${historyFolder}\n` +
   205â†’            `Linked to: ${symlinkPath}`
   206â†’        );
   207â†’
   208â†’        // Add to .gitignore if not present
   209â†’        await addToGitIgnore(projectPath);
   210â†’
   211â†’        // Setup Git filter for automatic cleaning
   212â†’        await setupGitFilter(projectPath, false); // false = don't show success message again
   213â†’
   214â†’    } catch (error: any) {
   215â†’        vscode.window.showErrorMessage(
   216â†’            `Failed to initialize Claude Code Chats Sync: ${error.message}`
   217â†’        );
   218â†’    }
   219â†’}
