   398â†’/**
   399â†’ * Clean sensitive information from session file content
   400â†’ *
   401â†’ * This removes API keys and other sensitive data while preserving
   402â†’ * the conversation structure for safe Git sharing.
   403â†’ */
   404â†’function cleanSensitiveData(content: string): string {
   405â†’    // Pattern for Anthropic API keys (normal format)
   406â†’    const apiKeyPattern = /"primaryApiKey"\s*:\s*"sk-ant-[^"]*"/g;
   407â†’
   408â†’    // Pattern for API keys within escaped JSON strings (like in tool results)
   409â†’    const apiKeyPatternEscaped = /\\"primaryApiKey\\":\s*\\"sk-ant-[^"]*\\"/g;
   410â†’
   411â†’    // Pattern for ANTHROPIC_AUTH_TOKEN (escaped format: \"ANTHROPIC_AUTH_TOKEN\": \"token\")
   412â†’    const authTokenPatternEscaped = /\\"ANTHROPIC_AUTH_TOKEN\\"\\s*:\\s*\\"[^"]*\\"/g;
   413â†’
   414â†’    // Pattern for other API keys
   415â†’    const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\s*:\s*"[^"]*"/gi;
   416â†’
   417â†’    // Clean Anthropic API keys (normal format)
   418â†’    let cleaned = content.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   419â†’
   420â†’    // Clean Anthropic API keys (escaped format in nested JSON)
   421â†’    cleaned = cleaned.replace(apiKeyPatternEscaped, '\\"primaryApiKey\\": \\"[REDACTED]\\"');
   422â†’
   423â†’    // Clean ANTHROPIC_AUTH_TOKEN (escaped format)
   424â†’    cleaned = cleaned.replace(authTokenPatternEscaped, '\\"ANTHROPIC_AUTH_TOKEN\\": \\"[REDACTED]\\"');
   425â†’
   426â†’    // Clean other API keys
   427â†’    cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   428â†’
   429â†’    return cleaned;
   430â†’}
   431â†’
   432â†’/**
   433â†’ * Clean all session files in the history folder
   434â†’ *
   435â†’ * This creates a cleaned version of all session files that can be
   436â†’ * safely committed to Git while keeping the original files intact.
   437â†’ */
   438â†’async function cleanSessionFiles(projectPath: string): Promise<void> {
   439â†’    const historyFolder = getHistoryFolderPath(projectPath);
   440â†’
   441â†’    if (!fs.existsSync(historyFolder)) {
   442â†’        vscode.window.showErrorMessage('History folder does not exist');
   443â†’        return;
   444â†’    }
   445â†’
   446â†’    const files = fs.readdirSync(historyFolder).filter(f => f.endsWith('.jsonl'));
   447â†’
   448â†’    if (files.length === 0) {
   449â†’        vscode.window.showInformationMessage('No session files to clean');
   450â†’        return;
   451â†’    }
   452â†’
   453â†’    let cleanedCount = 0;
   454â†’    const withProgress = vscode.window.withProgress;
   455â†’
   456â†’    await withProgress(
   457â†’        {
   458â†’            location: vscode.ProgressLocation.Notification,
   459â†’            title: 'Cleaning session files...',
   460â†’            cancellable: false
   461â†’        },
   462â†’        async () => {
   463â†’            for (const file of files) {
   464â†’                const filePath = path.join(historyFolder, file);
   465â†’                const content = fs.readFileSync(filePath, 'utf-8');
   466â†’                const cleaned = cleanSensitiveData(content);
   467â†’
   468â†’                // Write cleaned content back to file
   469â†’                fs.writeFileSync(filePath, cleaned, 'utf-8');
   470â†’                cleanedCount++;
   471â†’            }
   472â†’        }
   473â†’    );
   474â†’
   475â†’    vscode.window.showInformationMessage(
   476â†’        `âœ… Cleaned ${cleanedCount} session file(s). Sensitive data has been redacted.`
   477â†’    );
   478â†’}
