   480â†’/**
   481â†’ * Setup Git filter for automatic cleaning on commit
   482â†’ *
   483â†’ * This configures Git to automatically clean sensitive data from
   484â†’ * session files when they are committed.
   485â†’ *
   486â†’ * @param projectPath - The project root path
   487â†’ * @param showMessage - Whether to show success message (default: true)
   488â†’ */
   489â†’async function setupGitFilter(projectPath: string, showMessage: boolean = true): Promise<void> {
   490â†’    const config = vscode.workspace.getConfiguration('claudeCodeSync');
   491â†’    const folderName = config.get('historyFolderName', '.claudeCodeSessions');
   492â†’
   493â†’    try {
   494â†’        // Check if we're in a Git repository
   495â†’        const gitDir = path.join(projectPath, '.git');
   496â†’        if (!fs.existsSync(gitDir)) {
   497â†’            vscode.window.showWarningMessage(
   498â†’                'Not a Git repository. Git filter will not be configured.'
   499â†’            );
   500â†’            return;
   501â†’        }
   502â†’
   503â†’        // Create the clean filter script in workspace (committed to repo)
   504â†’        const filterScriptPath = path.join(projectPath, '.gitfilters', 'clean-sessions.js');
   505â†’        const filterDir = path.dirname(filterScriptPath);
   506â†’
   507â†’        if (!fs.existsSync(filterDir)) {
   508â†’            fs.mkdirSync(filterDir, { recursive: true });
   509â†’        }
   510â†’
   511â†’        const filterScript = `#!/usr/bin/env node
   512â†’const fs = require('fs');
   513â†’
   514â†’// Pattern for Anthropic API keys (normal format)
   515â†’const apiKeyPattern = /"primaryApiKey"\\s*:\\s*"sk-ant-[^"]*"/g;
   516â†’
   517â†’// Pattern for API keys within escaped JSON strings
   518â†’const apiKeyPatternEscaped = /\\\\\\"primaryApiKey\\\\\\"\\\\s*:\\\\\\s*\\\\\\"sk-ant-[^"]*\\\\\\"/g;
   519â†’
   520â†’// Pattern for ANTHROPIC_AUTH_TOKEN (escaped format: \\"ANTHROPIC_AUTH_TOKEN\\": \\"token\\")
   521â†’const authTokenPatternEscaped = /\\\\"ANTHROPIC_AUTH_TOKEN\\\\"\\\\s*:\\\\\\s*\\\\"[^"]*\\\\"/g;
   522â†’
   523â†’// Pattern for other API keys
   524â†’const genericApiKeyPattern = /"(apiKey|api_key|authorization|token|bearer)"\\s*:\\s*"[^"]*"/gi;
   525â†’
   526â†’let data = '';
   527â†’process.stdin.setEncoding('utf8');
   528â†’
   529â†’process.stdin.on('data', (chunk) => {
   530â†’    data += chunk;
   531â†’});
   532â†’
   533â†’process.stdin.on('end', () => {
   534â†’    let cleaned = data.replace(apiKeyPattern, '"primaryApiKey": "[REDACTED]"');
   535â†’    cleaned = cleaned.replace(apiKeyPatternEscaped, '\\\\\\"primaryApiKey\\\\\\": \\\\"[REDACTED]\\\\"');
   536â†’    cleaned = cleaned.replace(authTokenPatternEscaped, '\\\\\\"ANTHROPIC_AUTH_TOKEN\\\\\\": \\\\"[REDACTED]\\\\"');
   537â†’    cleaned = cleaned.replace(genericApiKeyPattern, '"$1": "[REDACTED]"');
   538â†’    process.stdout.write(cleaned);
   539â†’});
   540â†’`;
   541â†’
   542â†’        fs.writeFileSync(filterScriptPath, filterScript, 'utf-8');
   543â†’
   544â†’        // Make it executable on Unix-like systems
   545â†’        if (process.platform !== 'win32') {
   546â†’            try {
   547â†’                fs.chmodSync(filterScriptPath, 0o755);
   548â†’            } catch (e) {
   549â†’                // Ignore permission errors
   550â†’            }
   551â†’        }
   552â†’
   553â†’        // Configure Git to use the filter in .gitconfig (project-level, committed to repo)
   554â†’        const gitConfigPath = path.join(projectPath, '.gitconfig');
   555â†’
   556â†’        let gitConfig = '';
   557â†’        if (fs.existsSync(gitConfigPath)) {
   558â†’            gitConfig = fs.readFileSync(gitConfigPath, 'utf-8');
   559â†’        }
   560â†’
   561â†’        if (!gitConfig.includes('[filter "claude-clean"]')) {
   562â†’            if (gitConfig && !gitConfig.endsWith('\n')) {
   563â†’                gitConfig += '\n';
   564â†’            }
   565â†’            gitConfig += `[filter "claude-clean"]
   566â†’\tclean = node .gitfilters/clean-sessions.js
   567â†’`;
   568â†’            fs.writeFileSync(gitConfigPath, gitConfig, 'utf-8');
   569â†’        }
   570â†’
   571â†’        // Configure the filter in local Git config (this actually makes it work)
   572â†’        try {
   573â†’            execSync(
   574â†’                `git config filter.claude-clean.clean "node .gitfilters/clean-sessions.js"`,
   575â†’                { cwd: projectPath, stdio: 'pipe' }
   576â†’            );
   577â†’        } catch (error: any) {
   578â†’            vscode.window.showWarningMessage(
   579â†’                `Failed to configure local Git filter: ${error.message}`
   580â†’            );
   581â†’            // Don't return here, as .gitconfig was created successfully
   582â†’        }
   583â†’
   584â†’        // Configure Git to use the filter
   585â†’        const gitAttributesPath = path.join(projectPath, '.gitattributes');
   586â†’
